com.mirth.connect.plugins.javascriptstep.script:
var mapping;

try {
    mapping = msg;
    msg = (sourceMap.get('reprocessed') == true) ? JSON.parse($('rawMessage')) : $('rawMessage');
} catch (e) {
    mapping = '';
}

channelMap.put('Bundle', validate(JSON.stringify(mapping), '', new Array()));

var encounter = msg['visitaIntRefExt'];
//channelMap.put('visita', encounter);

var mappingVisita='';
try {
    for (var i=0; i < msg['episodioIdentificadores'].length ; i++) {
    		if (msg['episodioIdentificadores'][i]['sistemaCodificador'].toString().toLowerCase() == "encounter.id") {
    			mappingVisita = msg['episodioIdentificadores'][i]['codigo'].toString();
    			break;
    		}
    }
} catch (e) {
    mappingVisita = '';
}

if (mappingVisita == '' && encounter){
	mappingVisita = encounter;
}

channelMap.put('visita', validate(mappingVisita, '', new Array()));

var doenteId = msg['doente']['numSequencial'];
channelMap.put('doenteId', validate(doenteId, '', new Array()));

var nomeDoente = msg['doente']['nomeDoente'];
channelMap.put('nomeDoente', nomeDoente);

var requisicaoId = msg['requisicao']['requisicaoId'];
channelMap.put('requisicaoId', requisicaoId);

var requisicaoRefExt = msg['requisicao']['requisicaoRefExt'];
channelMap.put('requisicaoRefExt', requisicaoRefExt);

var hospitalRefExt = msg['episodio']['servicoRefExt'];
channelMap.put('hospitalRefExt', hospitalRefExt);

if (msg['requisicao']['anulado'] == true){
	channelMap.put('evento', "CancelPrescriptionExternal");
	channelMap.put('submissaoSistExterno', false); //não será para faturar (i.e, enviar CI como execução para o sistema externo)

} else {
	var submissaoSistExterno = false;

	/* Prioridade Default: urgent = normal */
	var prioridadeRefExt = msg['requisicao']['prioridadeRefExt'] != null ? msg['requisicao']['prioridadeRefExt'] : 'urgent';

	var exames = []; 
	if (prioridadeRefExt.equals('stat')){ // Rule: Se a prioridade é via verde (stat) e o serviço executante não é "Patologia Clínica", então deve ser criada uma mensagem de "ChargeItem"

		if (msg['requisicao']['exames'].length > 0 ){
			for (var i = 0; i < getArrayOrXmlLength(msg['requisicao']['exames']); i++) {

				if (msg['requisicao']['exames'][i]['faturavel'] != false && msg['requisicao']['exames'][i]['cdServicoExecutanteRefExt'] != "12" ){ // Patologia Clínica
					exames.push(msg['requisicao']['exames'][i]);
				}
			}
			msg['requisicao']['exames'] = exames;
		}

		if (msg['requisicao']['exames'].length != 0){
			// tem exames de Patologia Clinica, neste caso dispara o ChargeItem
			submissaoSistExterno = true;
		}
	} else {

		if (msg['requisicao']['exames'].length > 0 ){
			for (var i = 0; i < getArrayOrXmlLength(msg['requisicao']['exames']); i++) {
				if (msg['requisicao']['exames'][i]['faturavel'] != false && msg['requisicao']['exames'][i]['cdServicoExecutanteRefExt'] == "17" ){ // Anatomia Patológica
					exames.push(msg['requisicao']['exames'][i]);
				}
			}
			msg['requisicao']['exames'] = exames;

			if (msg['requisicao']['exames'].length != 0){
				// tem exames que nao sao de anatomia patológica, neste caso dispara o ChargeItem
				submissaoSistExterno = true;
			}

		}
	}
	channelMap.put('submissaoSistExterno', submissaoSistExterno);
	if (submissaoSistExterno) {
		channelMap.put('evento', "CreateAndExecutePrescriptionExternal");
	} else {
		channelMap.put('evento', "CreatePrescriptionExternal");
	}
}

if (msg['requisicao']['exames'].length > 0 ) { // só se adicionam CIs ao CarePlan se existirem exames a explorar

	var Bundle = JSON.parse($('Bundle'));

	var routingResp = router.routeMessageByChannelId("74dbb502-8da5-45ee-90ed-3ccc48afe44c", JSON.stringify(msg)); // FUNC_CREATE_CHARGE_ITEM
	if (routingResp != null && routingResp.getStatus() == Status.valueOf("SENT") ) {

		// ter em atenção que resource é uma lista (resource:[]) na entrada relativa ao ChargeItem!
		channelMap.put('BundleResponse', routingResp.getMessage()); //debugging

		BundleResp = JSON.parse( routingResp.getMessage() );
		if (BundleResp['entry'].length == 2) { //existem CIs para ser adicionados (vem MessageHader + lista de CIs)
			if (typeof Bundle['entry'][1]['resource']['contained'] == 'undefined') { // verifica se Careplan.contained já existe; caso não exista, cria vazio para adicionar CIs em baixo
				Bundle['entry'][1]['resource']['contained'] = [];
			}
			var chargeItemListIds = [];
			for (var i=0; i< BundleResp['entry'][1]['resource'].length; i++) { //lista de CIs
				Bundle['entry'][1]['resource']['contained'].push(BundleResp['entry'][1]['resource'][i]); // adicionar CI à prescrição (Bundle.contained)
				chargeItemListIds.push(BundleResp['entry'][1]['resource'][i]['id']);
			}
			channelMap.put('Bundle', JSON.stringify(Bundle));
			try {
				channelMap.put('bundleId', Bundle.id);
				channelMap.put('chargeItemListIds', JSON.stringify(chargeItemListIds));

			} catch (error) {
				channelMap.put('erroCustomMetadata', error.toString());
			}
		}

	} else {
		try {
			var erroRouting = routingResp.getError() != "" ? routingResp.getError() : routingResp.getMessage();
			channelMap.put('erroRouting', erroRouting);
		} catch (error2) {
			;
		}
	}

}

