com.mirth.connect.plugins.javascriptstep.script:
var isExameEspecial = false;
var error = false;

for (var i=0; i<msg['entry'].length; i++) {
	if (msg['entry'][i]['resource']['resourceType'] == "ServiceRequest") { // exames podem ter prescrição interna (vem com ServiceRequest+CarePlan) ou presricao externa (vem só com Appointment)

		msg['entry'][i]['resource']['code']['coding'].find(function(coding){
			if (coding['system'].includes("Service") && !coding['system'].includes("thom")) {
				serviceId = coding['code'];
			} else {
				serviceExtId = coding['code'];
			}
		});

		result = checkIfExameEspecial(serviceId, serviceExtId);
		if (result.statusCode == "500") {
			error = true;
		} else {
			isExameEspecial = result.responseMessage; // true or false
		}


	}else if (msg['entry'][i]['resource']['resourceType'] == "Appointment"){

		//só se aplica a ambulatório
		msg['entry'][i]['resource']['serviceCategory'].find( function(serviceCat) {
			serviceCat['coding'].find( function(coding) {
				if (coding['code'] == $('ambulatoryServiceCategory') ) { // Ambulatório


					var serviceId = null;
					var serviceExtId = null;
					msg['entry'][i]['resource']['serviceType'][0]['coding'].find( function(coding) {
						if (coding['system'].includes("Service") && !coding['system'].includes("thom")) {
							serviceId = coding['code'];
						} else {
							serviceExtId = coding['code'];
						}
					})

					result = checkIfExameEspecial(serviceId, serviceExtId);

					if (result.statusCode == "500") {
						error = true;
					} else {
						isExameEspecial = result.responseMessage; // true or false
					}							
				}
			})
		})

	}
}

if (error) {
	channelMap.put('errorExameEspecial', error);
} else {
	channelMap.put('isExameEspecial', isExameEspecial);
}

