// Get a database connection
var dbConn = DatabaseConnectionFactory.createDatabaseConnection('org.postgresql.Driver', 'jdbc:postgresql://' + $('DB_transactions_host') + ':' + $('DB_transactions_port') + '/postgres', $('DB_transactions_user'), $('DB_transactions_pass'));

