// Get a database connection
var dbConn = DatabaseConnectionFactory.createDatabaseConnection('org.postgresql.Driver', 'jdbc:postgresql://' + $('DB_transactions_host') + ':' + $('DB_transactions_port') + '/int_mirth_transaction', $('DB_transactions_user'), $('DB_transactions_pass'));

// Define the SQL query to create the table

var createTableSQL = "CREATE TABLE IF NOT EXISTS service_request_external_ids ("
                   + "service_request_id character varying(255) PRIMARY KEY,"
                   + "hl7_placer character varying(255)"
                   + ");";

// Define the SQL query to create an index on service_request_id column
var createIndexQuery = "CREATE INDEX IF NOT EXISTS idx_service_request_id ON service_request_external_ids (service_request_id, hl7_placer);";

// encounter_external_ids - Define the SQL query to create the table

var createTableSQLEncounterIds = "CREATE TABLE IF NOT EXISTS encounter_external_ids ("
                   + "encounter_id character varying(255) PRIMARY KEY,"
                   + "encounter_identifier_value character varying(255)"
                   + ");";

// encounter_external_ids Define the SQL query to create an index on service_request_id column
var createIndexQueryEncounterIds = "CREATE INDEX IF NOT EXISTS idx_encounter_identifier_value ON encounter_external_ids (encounter_identifier_value);";


try {
    // Execute the SQL query
    dbConn.executeUpdate(createTableSQL);
    dbConn.executeUpdate(createIndexQuery);
    logger.info("Table created successfully.");
    dbConn.executeUpdate(createTableSQLEncounterIds);
    dbConn.executeUpdate(createIndexQueryEncounterIds);
    logger.info("Table encounter_external_ids created successfully.");
} catch (e) {
	if (e.message.includes('already exists')){
		logger.error("Table already exists");	
	}else {
		logger.error("Error creating Table: " + e);	
	}

} finally {
    // Close the database connection
    dbConn.close();
}


return;

