		} else {
			logger.info("excecao1");
			lista= [];
		}
	} catch (err) {
		logger.info("excecao2" + err.toString());
		lista =  [];
	}
	/*
	if (! Array.isArray(listaAdicionar)) {
		lista = [];
	} else {
		lista = JSON.parse( JSON.stringify(listaAdicionar));
	}
	*/
	if (typeof(sistCodificador) == "string" && sistCodificador != "") {
		obj = new Object();
		obj.sistemaCodificador = sistCodificador;
