/**
	Generic code to implement API call

	@param {String} method - http method
	@param {String} url - http url
	@param {String} user - api username
	@param {String} pass - api password
	@return {Object} return api response
*/
function callApi(method, url, user, pass) {

	var url = new Packages.java.net.URL(url);
	var connection = url.openConnection();
	connection.setRequestMethod(method);

	try {
	  var responseCode = connection.getResponseCode();
	  if (responseCode === Packages.java.net.HttpURLConnection.HTTP_OK) {	//	200
	    var inputStream = connection.getInputStream();
	    var reader = new Packages.java.io.BufferedReader(new Packages.java.io.InputStreamReader(inputStream));
	    var response = ""; // Inicializa a variável response

		    while ((line = reader.readLine()) !== null) {
		      response += line;
		    }      
	        reader.close();	      

		   return JSON.parse(response.toString());

	    } else {
	        var errorMessage = "Erro na chamada ao Web Service: " +  url + " Código de resposta: " + responseCode;
	        // Trata os casos em que a chamada ao Web Service não foi bem-sucedida        
	        throw new Error(errorMessage);
	    }
	} catch (e) {
	    // Capturar exceção e retornar status e mensagem de erro
	    var errorStatus = connection.getResponseCode();
//	    var errorMessage = e.getMessage();	    
	    throw new Error(errorStatus);
	}
}

/**
	Função cujo objetivo passa por receber um código identificativo de um exame (ou outro serviço qualquer) e retornar se o mesmo é um exame especial ou não. Para
	tal, a função vai à tabela de catálogo da BD verificar se o código lá existe. Caso haja algum problema de acesso à BD, e de forma opcional, a função tenta aceder
	ao endpoint do sistema externo para fazer a verificação de exame especial.

	@param {String} catalogCode - código de catálogo CUF a ser analisado
	@param {String} externalSysCode - código de catálogo do sistema externo a ser analisado
	@return {Object} return object
*/
function checkIfExameEspecial(catalogCode, externalSysCode) {
	var error = false;
	var errorMsg = null;
	var isExameEspecial = null;
	var resposta = new Object();

	// Get a database connection 
	var dbConn = DatabaseConnectionFactory.createDatabaseConnection('org.postgresql.Driver', 'jdbc:postgresql://' + $('DB_transactions_host') + ':' + $('DB_transactions_port') + '/int_mirth_transaction', $('DB_transactions_user'), $('DB_transactions_pass'));
	var selectQuery = "SELECT * FROM \"examesEspeciais\".\"CD_ExamesEspeciais\" WHERE \"intRefExt\" = '" + catalogCode + "' and ativo = true;";
	try {
		// Execute the SQL query
		result = dbConn.executeCachedQuery(selectQuery);
		if (result.next() ) { // true
			isExameEspecial = true;
		} else { 
			isExameEspecial = false; // false
		}
	} catch (e) {
		error = true;
		logger.info("e.toString() = " + e.toString());
		errorMsg = e.toString();
	} finally {
	    // Close the database connection
	    dbConn.close();
	}

	//houve erro no acesso à BD, logo tenta aceder ao endpoint do sistema externo para obter essa informação
	if (isExameEspecial == null && typeof externalSysCode != 'undefined') {
		var httpPostURL = $('Transactional_CUFBUS_Host') + "CodeSystem/Service/$lookup";
		var body = new Object();
		body.resourceType= "Parameters";
		body.parameter = [{"name":"code","valueCode":externalSysCode}];
		var authHeader = null
		if ($('Transactional_CUFBUS_User') != null && $('Transactional_CUFBUS_Pass') != ''){
			authHeader = apiGenerateBasicAuthHeader($('Transactional_CUFBUS_User'), $('Transactional_CUFBUS_Pass'));  
		}
		httpResp = httpPost(httpPostURL,JSON.stringify(body),authHeader);

		if(httpResp.statusCode === "200"){
			if(isJSON(httpResp.responseMessage)){
				var parametersRes = JSON.parse(httpResp.responseMessage);
				try {
					if (parametersRes['resourceType'] == "Parameters") {
						var checkExameEspecial = null;
						parametersRes['parameter'].find( function(parameter) {
							if(parameter['name'] == "property") {
								parameter['part'].find( function(part) {
									if (part['valueCoding']) {
										if (part['valueCoding']['code'] == $('Catalogo_ServiceCategory_ExameEspecial')) {
											isExameEspecial = true;
											checkExameEspecial = true;
											return true;
										}
									}
								})
							}
						})
						if (checkExameEspecial == null) { // não encontrou property de exame especial, logo não é exame especial
							isExameEspecial = false;
						}
					} else {
						error = true;
						errorMsg = "A Parameters Resource was expected as a response";
					}
				} catch (e2) {
					error = true;
					errorMsg = "Error parsing the response from the endpoint " + e2.toString();
				}
			} else {
				error = true;
				errorMsg = "Endpoint response was not JSON";
			}
		} else {
			error = true;
			errorMsg = httpResp.statusText ? httpResp.statusText : (httpResp.responseMessage ? httpResp.responseMessage : null);;		
		}
	}
	if (isExameEspecial == null) {
		resposta.statusCode = "500";
		resposta.responseMessage = errorMsg; //string
	} else {
		resposta.statusCode = "200";
		resposta.responseMessage = isExameEspecial; // boolean
	}
	return resposta;
}

/**
	This function receive a date in format "yyyyMMdd" and return a date string in formart "yyyy-MM-ddTHH:mm:ss.sssZ"

	@param {String} date - date in format "yyyyMMdd"
	@return {String} return datetime string in format "yyyy-MM-ddTHH:mm:ss.sssZ"
*/
function convertDateToTimeStamp(date) {

	var dateYear = date.substring(0, 4);
	var dateMonth = date.substring(4, 6);
	var dateDay = date.substring(6, 8);

	var hour = '00';
	var min = '00';
	var sec = '00';

	return dateYear.concat('-').concat(dateMonth).concat('-').concat(dateDay).concat('T').concat(hour).concat(":").concat(min).concat(":").concat(sec).concat(".").concat("000Z");
}

/**
	This function receive a date in format "yyyyMMdd" and time "HHmmss" and return a date string in formart "yyyy-MM-ddTHH:mm:ss.sssZ"

	@param {String} date - date in format "yyyyMMdd"
	@param {String} time - time in format "HHmmss"
	@return {String} return datetime string in format "yyyy-MM-ddTHH:mm:ss.sssZ"
*/
function convertTimeStampToDateStr(date, time) {

	if (time.length <= 5){ // left pad
		time = time.padStart(time.length + 1, '0');
	}

	var dateYear = date.substring(0, 4);
	var dateMonth = date.substring(4, 6);
	var dateDay = date.substring(6, 8);

	var hour = time.substring(0, 2);
	var min = time.substring(2, 4);
	var sec = time.substring(4, 6);

	return dateYear.concat('-').concat(dateMonth).concat('-').concat(dateDay).concat('T').concat(hour).concat(":").concat(min).concat(":").concat(sec).concat(".").concat("000Z");
}

/**
	Convert Date to UTC

	@param {String} date - Date string in format "yyyy-MM-ddTHH:mm:ss+SSSS"
	@return {String} return date in UTC "yyyy-MM-ddTHH:mm:ss+Z"
*/
function DateUtc(date, dateFormat) {

     var formatter_hl7 = new java.text.SimpleDateFormat(dateFormat != null ? dateFormat : "yyyy-MM-dd'T'HH:mm:ss+SSSS");
     formatter_hl7.setTimeZone(java.util.TimeZone.getTimeZone($('serverTimeZone')));
     var formatter_utc = new java.text.SimpleDateFormat("yyyy-MM-dd'T'HH:mm:ss'Z'");
     formatter_utc.setTimeZone(java.util.TimeZone.getTimeZone("UTC"));
	return formatter_utc.format(formatter_hl7.parse(date));			
}

/**
	Formata uma data string num formato pretendido independentemente do formato do input do date string

	@param {String} dateString
	@param {String} mask
	@return {String} return date formarted by mask
*/
function formatDateByMask(dateString, mask) {
    var parts = dateString.split(/[-T:.Z+]/);

    var year = parts[0];
    var month = parts[1];
    var day = parts[2];
    var hours = parts[3];
    var minutes = parts[4];
    var seconds = parts[5];

    return mask.replace(/yyyy|MM|dd|HH|mm|ss/g, function (match) {
        switch (match) {
            case "yyyy":
                return year;
            case "MM":
                return month;
            case "dd":
                return day;
            case "HH":
                return hours;
            case "mm":
                return minutes;
            case "ss":
                return seconds;
            default:
                return match;
        }
    });
}

/**
	Função criadora de id de mensagem HL7 v2 (MSH.10.1) único v5

	@return {String} id unico
*/
function generate_HLv2_messageId(tipo_mensagem) {

	var timestamp = new Date().getTime().toString();
	var timestamp_length = timestamp.length;

	//random remaining characters
	remainder = 20-3-timestamp_length;

	// Generate a random number between 0... and 9...
	var randomNumber = Math.floor(Math.random() * Math.pow(10,remainder));
	// Convert the number to a string and pad it with leading zeros if needed
	var formattedNumber = randomNumber.toString().padStart(remainder, '0');

	return timestamp + "-" + tipo_mensagem[0] +"-" + formattedNumber;
}

/**
	This function returns unique values from an array

	@param {array} array - array
	@return {array} return unique values from array
*/
function getArrayUniqueValues(array) {
	var uniqueArray = [];
	for (var i = 0; i < array.length; i++) {
	    if (uniqueArray.indexOf(array[i]) === -1) {
	        uniqueArray.push(array[i]);
	    }
	}

	return uniqueArray;
}

/**
	Returns the Encounter Identifer Value by UUID Episode.

	@param {String} encounterId - UUID Episode
	@return {String} return Encounter Identifer Value
*/
function getEncounterIdentifierValueByEncounterId(encounterId){
	var res = null;
	// Define the PostgreSQL JDBC URL, username, and password
	var url = "jdbc:postgresql://" + $('DB_transactions_host') + ":" + $('DB_transactions_port') + "/" + $('DB_transactions_name');
	var username = $('DB_transactions_user');
	var password = $('DB_transactions_pass');

	// Load the PostgreSQL JDBC driver
	java.lang.Class.forName("org.postgresql.Driver");

	// Establish a database connection
	var conn = java.sql.DriverManager.getConnection(url, username, password);

	try {
	    // Define the SQL query to retrieve the value by ID
	    var getValueQuery = "SELECT encounter_identifier_value FROM encounter_external_ids WHERE encounter_id = ?";

	    // Create a prepared statement
	    var pstmt = conn.prepareStatement(getValueQuery);
	    pstmt.setString(1, encounterId);

	    // Execute the query and retrieve the result
	    var rs = pstmt.executeQuery();

	    if (rs.next()) {
	        var encounterIdentifierValue = rs.getString("encounter_identifier_value");
	        res = encounterIdentifierValue;
	    } else {
	        logger.info("No value found for ID " + encounterId);
	    }
	} catch (e) {
	    logger.error("Error retrieving value: " + e);
	} finally {
	    // Close the resources
	    if (rs) rs.close();
	    if (pstmt) pstmt.close();
	    if (conn) conn.close();
	}

	return res;
}

/**
	This function returns first name of a string containing full name

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
function getFirstName(fullName) {
	var names = fullName.split(' ');
	return names[0];
}

/**
	Extrai da listagem de identificadores o "codigo" associado a um pretendido "sistCodificador"

	@param {String} listaIds - listagem de identificadores onde procurar
	@param {String} sistCodificador - sistemaCodificador a ser procurado na listagem de identificadores
	@return {String} return codigo (ou null, caso não exista)
*/
function getIdentificador(listaIds, sistCodificador) {
	var codigo = null;	
	try{  		
	    	listaIds.find(function(elemento) {
	    		if (elemento.sistemaCodificador){
	    			try { // para o caso de algum dos identificadores estar mal definido não impedir que se extraia a informação pretendida
		        		if (elemento.sistemaCodificador.toLowerCase() == sistCodificador.toLowerCase()){
		          		codigo= elemento.codigo;
		          		return true;
		        		}
	    			} catch (err) {
		        		;
		        	}
	      	}
	    });
    	} catch (ex1){
    		codigo = null;
    	}
	return codigo;
}

/**
	This function returns last name of a string containing full name

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
function getLastName(fullName) {
	var names = fullName.split(' ');
	return names[(names.length -1) ];
}

/**
	Função cujo propósito passa por, dada informação sobre o hospital, o código de catálogo de exame especial e a especialidade, retornar qual a localização no pretendido hospital predestinada para
	a ocorrência de exames especiais dessa mesma especialidade

	@param {String} hospitalINTRefExt - INTRefExt do hospital (CMFL, HTEJO, ...)
	@param {String} catalogCode - código de catálogo do exame especial (10058, 20056, ...)
	@param {String} especialidade - especialidade associada ao exame especial (Cardiologia, Oftalmologia, ...)
	@return {String} return {"servico": "...", "area": "...", "sala": "..." "cama": "..."}
*/
function getLocationExameEspecial(hospitalINTRefExt, catalogCode, especialidade) {
	var error = false;
	var errorMsg = null;
	var resposta = new Object();

	var cama = null;
	var sala = null;
	var area = null;
	var servico = null;

	if ( !hospitalINTRefExt || !catalogCode || !especialidade ) {
		error = true;
		errorMsg = "Um dos parametros da função é invalido";
	} else {

		var dbConn = DatabaseConnectionFactory.createDatabaseConnection('org.postgresql.Driver', 'jdbc:postgresql://' + $('DB_transactions_host') + ':' + $('DB_transactions_port') + '/int_mirth_transaction', $('DB_transactions_user'), $('DB_transactions_pass'));

		/* var selectQuery = "SELECT cama, sala, area FROM \"examesEspeciais\".\"LocalizacaoExamesEspeciais\" WHERE servico = '" + AAAAAAAAA + "' AND especialidade = '"+ especialidade + "' AND id ="
						+ "(SELECT \"idLocalizacao\" FROM \"examesEspeciais\".\"LigacaoLocalizacaoExamesEspeciais\" WHERE ativo = true AND \"hospitalINTRefExt\" = '" + hospitalINTRefExt + "' AND  \"idExameEspecial\" ="
						+ "(SELECT id FROM \"examesEspeciais\".\"CD_ExamesEspeciais\" where intRefExt = '" + catalogCode + "' LIMIT 1) LIMIT 1) LIMIT 1;"; */

		var selectIdLocalizacaoSQL = "SELECT \"idLocalizacao\" FROM \"examesEspeciais\".\"LigacaoLocalizacaoExamesEspeciais\" WHERE ativo = true AND \"hospitalINTRefExt\" = '" + hospitalINTRefExt + "' AND  \"idExameEspecial\" ="
						+ "(SELECT id FROM \"examesEspeciais\".\"CD_ExamesEspeciais\" where \"intRefExt\" = '" + catalogCode + "' LIMIT 1) ;";

		var listIdLocalizacao= [];
		try {
			// Execute the SQL query
			result = dbConn.executeCachedQuery(selectIdLocalizacaoSQL); // return javax.sql.rowset.CachedRowSet
			while (result.next()) {
				listIdLocalizacao.push(result.getString("idLocalizacao"));
			}
		} catch (e) {
			error = true;
			logger.info("e.toString() = " + e.toString());
			errorMsg = e.toString();
		} finally {
		    // Close the database connection
		    dbConn.close();
		}
		logger.info("lsita ids localizacao = " +JSON.stringify(listIdLocalizacao));


		if (listIdLocalizacao.length == 0){ // não existem idLocalizacao configurados para o exame
			error = true;
			errorMsg = "Não existem idLocalizacao para o código de catálogo " + catalogCode + " e hospital " + hospitalINTRefExt;
		} else {
			var dbConn = DatabaseConnectionFactory.createDatabaseConnection('org.postgresql.Driver', 'jdbc:postgresql://' + $('DB_transactions_host') + ':' + $('DB_transactions_port') + '/int_mirth_transaction', $('DB_transactions_user'), $('DB_transactions_pass'));
			if (listIdLocalizacao.length == 1) { //só existe 1 idLocalizacao, logo não interessa a especialidade porque a localizacao do exame especial é sempre no mesmo local
				var selectInfosSQL = "SELECT cama, sala, area, servico FROM \"examesEspeciais\".\"LocalizacaoExamesEspeciais\" WHERE id = '" + listIdLocalizacao[0] + "';";
				try {
					// Execute the SQL query
					result = dbConn.executeCachedQuery(selectInfosSQL); // return javax.sql.rowset.CachedRowSet
					while (result.next()) {
						cama = result.getString("cama");
						sala = result.getString("sala");
						area = result.getString("area");
						servico = result.getString("servico");
					}
				} catch (e) {
					error = true;
					logger.info("e.toString() = " + e.toString());
					errorMsg = e.toString();
				} finally {
				    // Close the database connection
				    dbConn.close();
				}			
			} else { // existe mais do que uma localização, logo vai diferenceiar por especialidade
				ids = "(";
				for (var i=0; i< listIdLocalizacao.length; i++) {
				    ids += "'" + listIdLocalizacao[i].toString() + "',";
				}
				ids = ids.slice(0,-1) + ")";
				var selectInfosSQL = "SELECT cama, sala, area, servico FROM \"examesEspeciais\".\"LocalizacaoExamesEspeciais\" WHERE id in " + ids + " and especialidade = '"+especialidade+ "';";
				logger.info(selectInfosSQL);
				try {
					// Execute the SQL query
					result = dbConn.executeCachedQuery(selectInfosSQL); // return javax.sql.rowset.CachedRowSet
					while (result.next()) {
						cama = result.getString("cama");
						sala = result.getString("sala");
						area = result.getString("area");
						servico = result.getString("servico");
					}
				} catch (e) {
					error = true;
					logger.info("e.toString() = " + e.toString());
					errorMsg = e.toString();
				} finally {
				    // Close the database connection
				    dbConn.close();
				}	
			}
		}
	}


	if (error == true  ) {
		resposta.statusCode = "500";
		resposta.responseMessage = errorMsg ? errorMsg : "Erro interno getLocationExameEspecial()";
	} else if ( cama == null && sala == null && area == null  && servico == null) {
		resposta.statusCode = "500";
		resposta.responseMessage = errorMsg ? errorMsg : "Erro na query à LocalizacaoExamesEspeciais";
	}else {
		resposta.statusCode = "200";
		var respObj = new Object();
		respObj.cama = cama;
		respObj.area = area;
		respObj.sala = sala;
		respObj.servico = servico;
		resposta.responseMessage = respObj;
	}
	return resposta;

}

/**
	Funcao usada no canal "CONSUMOS_OUT" dos agendamentos, para obter do array de utilizadores o utilizador por ID de alteracao ou criacao.

	@param {Int} id - Utilizador ID
	@return {String} return NumMecanografico do utilizador presente no array de utilizadores
*/
function getNumMecanograficoById(utilizadores, id) {
  for (var i = 0; i < utilizadores.length; i++) {
    var utilizador = utilizadores[i];
    if (utilizador.id === id) {
      return utilizador.numMecanografico;
    }
  }
  return null; // Return null if no matching id is found
}

/**
	Funcao usada no canal "CONSUMOS_OUT" dos agendamentos, para obter do array de utilizadores o utilizador por ID de alteracao ou criacao.

	@param {Int} id - Utilizador ID
	@return {String} return NumMecanografico do utilizador presente no array de utilizadores
*/
function getPerfilUtilizadorById(utilizadores, id) {
  for (var i = 0; i < utilizadores.length; i++) {
    var utilizador = utilizadores[i];
    if (utilizador.id === id) {
      return utilizador.perfil;
    }
  }
  return null; // Return null if no matching id is found
}

/**
	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {Object} accoesList - ...
	@param {Object} dataCriacaoRequisicao - ...
	@param {Object} utilizadoresList - ...
	@return {Object} return description

	(//R = obrigatório)
	--> Formato esperado acoesList: [
          {
            "utilizadorId" : 12284, //R
            "funcao" : 1, //R
            "data" : "2024-05-08T14:41:51.089035Z" //R
          },
          {
            "utilizadorId" : 12284,
            "funcao" : 3,
            "data" : "2024-05-08T14:27:58.349313Z"
          }
        ]

     --> Formato esperado utilizadoresList: [
	    {
	      "id" : 12284,
	      "login" : "MDPIRES",
	      "nome" : "Marcos Pires",
	      "numMecanografico" : "263812278",
	      "numOrdem" : "123",
	      "numFuncionario" : null,
	      "perfil" : 1,
	      "intRefExt" : null
	    }
	  ]

*/
function getPerformerAndDatesRequisicaoMcdts(accoesList,dataCriacaoRequisicao, utilizadoresList) {

	logger.info("accoesList - " +JSON.stringify(accoesList));
	logger.info("dataCriacaoRequisicao - " + JSON.stringify(dataCriacaoRequisicao));
	logger.info("utilizadoresList - " + JSON.stringify(accoesList));
	returnObj = new Object();

	var Performer = Lists.list();
	var occurenceDateTime = null;
	var dataValidacao = null;
	var dataRelato = null;
	for (var j = 0; j < getArrayOrXmlLength(accoesList); j++ ){

	      try {
			if (accoesList[j]['funcao'] == 1 /* Relato */){
				dataRelato = accoesList[j]['data'];
			} else if (accoesList[j]['funcao'] == 2 /* Validacao */){
				dataValidacao = accoesList[j]['data'];
			} else {
				occurenceDateTime = dataCriacaoRequisicao;
			}
	       } catch (error) {
	       	logger.info("getPerformerAndDatesRequisicaoMcdts -> erro nas datas - " + error.toString()); 
	     }

		try {
			  Performer.add({
			    function: {
			        coding: [{
			          system: newStringOrUndefined($('FhirServer_CUFBUS_Namespace') + "/fhir/CodeSystem/PersonFunction"),
			          code: newStringOrUndefined( accoesList[j]['funcao'] == 3 /* Executante */ ? mapPerfil(getPerfilUtilizadorById(utilizadoresList, accoesList[j]['utilizadorId'])) : mapFuncao(accoesList[j]['funcao']) )
			        }]
			      },
			    actor: {
			      reference: newStringOrUndefined(accoesList[j]['utilizadorId'] != null ? ("Practitioner/" + getNumMecanograficoById(utilizadoresList, accoesList[j]['utilizadorId']) ) : null),
			      identifier: {
			        use: newStringOrUndefined('official'),
			        type: {
			          coding: [{
			            system: newStringOrUndefined($('FhirServer_CUFBUS_Namespace') + "/fhir/CodeSystem/PersonIdentifierType"),
			            code: newStringOrUndefined('4')
			          }],
			          text: newStringOrUndefined('N.\u00BA mecanogr\u00E1fico')
			        },
			        value: newStringOrUndefined(accoesList[j]['utilizadorId'] != null ? getNumMecanograficoById(utilizadoresList, accoesList[j]['utilizadorId']) : null)
			      }
			    }
		  });
		} catch (error2) {
			logger.info("getPerformerAndDatesRequisicaoMcdts -> erro no performer - " + error2.toString()); 
		}
	}

	returnObj.performer = Performer;
	returnObj.occurenceDateTime = occurenceDateTime;
	returnObj.dataValidacao = dataValidacao;
	returnObj.dataRelato = dataRelato;

	return returnObj;

}

/**
	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {Void}
	@return {HostsDto} return hosts
*/
function getRabbitMqHosts() {
	// Set RabbitMQ Hosts
	importPackage(com.mirth.rabbitmq.client.dto);
	hosts = [];
	addr = new HostsDto(); // Main Host
	addr.setHost($('RabbitMqHost'));
	addr.setPort(parseInt($('RabbitMqPort')));
	hosts.push(addr);

	return hosts;
}

/**
	This function is used to check if a string is a valid JSON

	@param {String} string to validate if is JSON
	@return {Boolean} return boolean
*/
function isJSON(str) {
  try {
        JSON.parse(str);
    } catch (e) {
        return false;
    }
    return true;
}

/**
	This function is used to check if a value (string or number) is numeric

	@param {String} value to validate if is numeric
	@return {Boolean} return boolean
*/
function isNumeric(num) {
  if (typeof num == "string") { 
    return !isNaN(num) &&
           !isNaN(parseFloat(num));
  } else if (typeof num == "number") {
  	return true;
  } else {
  	return false;
  }
}

/**
	This function is used to create multiple VAR Segments according Initial Date and CD_PrescricaoFrequencias

	@param {String} freqUni - CD_PrescricaoFrequencias description code
	@param {String} dateInit - Date HL7 string: start prescription date
	@param {String} DateEnd - Date HL7 string: end prescription date
	@return {String} return VAR Segment
*/
function MappFrequencies(freqUni, dateStart, DateEnd) {

	startDate = "20230227115700";
	hora = startDate.substring(10, 8);
	freqfactor = 8;
	freqUnit = 'Dias';

	if (freqUnit == 'Hora'){ // x em x horas
	    // calc hours segments
	    if (startDate.length > 0 && freqfactor > 1){
	        console.log("running ...");
	        for (i = 0; i < (24 / freqfactor) ; i++){
	            if ( (parseInt(hora) + (freqfactor * i) - 24) >= 0  ){
	                hora = parseInt(hora) + (freqfactor * i) - 24;
	                break;
	            }
	        }

	        if ((24 / freqfactor) >= 1){
	            for(i = 0; i < (24 / freqfactor); i++){
	                console.log( (parseInt(hora) + (freqfactor * i)).toString().padStart(2, "0") + "0000" );
	            }
	        }else {
	            console.log( parseInt(hora).toString().padStart(2, "0") + "0000" );
	        }   
	    }
	} else if (freqUnit == 'Dias'){ // x em x dias
	    console.log( hora.padStart(2, "0") + "0000")
	}else if (freqUnit == 'Dias da Semanas' || freqUnit == 'Por Dia' || freqUnit == 'Refeições' || freqUnit == 'Dia/Hora'){ // têm de ser criados a cada dia
	    console.log( hora.padStart(2, "0") + "0000")
	}else if (freqUnit == 'Única'){ // Hora fixa^5
	    console.log( hora.padStart(2, "0") + "0000")
	}else if (freqUnit == 'SOS'){
	    console.log( null )
	}else if (freqUnit == 'Turno Fixo') {
	    // tenho de pedir ao bruno para ter alguma informação relacionada com os turnos
	}else if (freqUnit == 'Contínuo' || freqUnit == 'Sem horário'){ // Sem hor\XE1\rio^0
	    console.log( null )
	}

}

/**
	Setting the Encounter Identifier Value by Encounter Id (returns true: inserted; false: not inserted)

	@param {String} encounterIdentifierValue - Encounter Identifier Value
	@param {String} encounterId - Encounter Id
	@return {Boolean} return true if inserts or false if not
*/
function setEncounterIdentifierValueByEncounterId(encounterIdentifierValue, encounterId){

	if (getEncounterIdentifierValueByEncounterId(encounterId) != null) {
		updateEncounterIdentifierValueByEncounterId(encounterIdentifierValue, encounterId);
	} else { // set/insert

		var res = null;
		// Define the PostgreSQL JDBC URL, username, and password
		var url = "jdbc:postgresql://" + $('DB_transactions_host') + ":" + $('DB_transactions_port') + "/" + $('DB_transactions_name');
		var username = $('DB_transactions_user');
		var password = $('DB_transactions_pass');

		// Load the PostgreSQL JDBC driver
		java.lang.Class.forName("org.postgresql.Driver");

		// Establish a database connection
		var conn = java.sql.DriverManager.getConnection(url, username, password);

		try {
			// Define the SQL query to retrieve the value by ID
			// var insertQuery = "INSERT INTO episod_ids_mapping (human_episod_id, uuid_episod) VALUES (?,?) ON CONFLICT (uuid_episod) DO NOTHING"; // ?? trocar isto
			var insertQuery = "INSERT INTO encounter_external_ids (encounter_identifier_value, encounter_id) VALUES (?,?)";

			// Create a prepared statement
			var pstmt = conn.prepareStatement(insertQuery);
			pstmt.setString(1, encounterIdentifierValue);
			pstmt.setString(2, encounterId);

			// Execute the query and retrieve the result
			var rowsInserted = pstmt.executeUpdate();

			if (rowsInserted > 0) {
				   res = true;
			        //logger.info("New row inserted with value: " + newValue);
			    } else {
			    	   res = false;
			        //logger.info("No rows inserted.");
			    }
			} catch (e) {
			    logger.error("Error inserting row: " + e);
			    res = false;
			} finally {
			    // Close the resources
			    if (pstmt) pstmt.close();
			    if (conn) conn.close();
			}


		return res;
	}
}

/**
	Updating the Encounter Identifier Value by Encounter Id (returns true: inserted; false: not updated)

	@param {String} encounterIdentifierValue - Encounter Identifier Value
	@param {String} encounterId - Encounter Id
	@return {Boolean} return true if updates or false if not
*/
function updateEncounterIdentifierValueByEncounterId(encounterIdentifierValue, encounterId){
	var res = null;
	// Define the PostgreSQL JDBC URL, username, and password
	var url = "jdbc:postgresql://" + $('DB_transactions_host') + ":" + $('DB_transactions_port') + "/" + $('DB_transactions_name');
	var username = $('DB_transactions_user');
	var password = $('DB_transactions_pass');

	// Load the PostgreSQL JDBC driver
	java.lang.Class.forName("org.postgresql.Driver");

	// Establish a database connection
	var conn = java.sql.DriverManager.getConnection(url, username, password);

	try {
		// Define the SQL query to retrieve the value by ID
		var insertQuery = "UPDATE encounter_external_ids set encounter_identifier_value = ? where encounter_id = ?;"; // ?? trocar

		// Create a prepared statement
		var pstmt = conn.prepareStatement(insertQuery);
		pstmt.setString(1, encounterIdentifierValue);
		pstmt.setString(2, encounterId);

		// Execute the query and retrieve the result
		var rowsUpdated = pstmt.executeUpdate();

		if (rowsUpdated > 0) {
			   res = true;
		        //logger.info("New row inserted with value: " + newValue);
		    } else {
		    	   res = false;
		        //logger.info("No rows inserted.");
		    }
		} catch (e) {
		    logger.error("Error inserting row: " + e);
		    res = false;
		} finally {
		    // Close the resources
		    if (pstmt) pstmt.close();
		    if (conn) conn.close();
		}


	return res;
}

