	dataInicio - formatDateByMask(msg['PV1']['PV1.44']['PV1.44.1'].toString(),"yyyy-MM-dd'T'HH:mm:ss'Z'"); // ou yyyy-MM-dd'T'HH:mm:ss.SSSSSSSXXX
	dataFim
	sendingFacility - MSH.4
