var isInicioEpisodio = false;
for (var i =0; i < getArrayOrXmlLength(msg['NTE']); i++) {
	if (msg['NTE'][i]['NTE.4']['NTE.4.2'].toLowerCase() == "encounter.id" && msg['NTE'][i]['NTE.3']['NTE.3.1'] != "") {
		channelMap.put('visitaIntRefExt', msg['NTE'][i]['NTE.3']['NTE.3.1'].toString());
	}
	if (msg['NTE'][i]['NTE.4']['NTE.4.2'].toLowerCase() == "eventoid" && msg['NTE'][i]['NTE.3']['NTE.3.1'] != "") {
		channelMap.put('eventoId', msg['NTE'][i]['NTE.3']['NTE.3.1'].toString());
	} 
	if (msg['NTE'][i]['NTE.4']['NTE.4.2'].toLowerCase() == "tipo_mensagem" && msg['NTE'][i]['NTE.3']['NTE.3.1'].toLowerCase() == "mcdt") {
		isExameEspecial = true;
	}
	if (msg['NTE'][i]['NTE.4']['NTE.4.2'].toLowerCase() == "chargeitem.id" && msg['NTE'][i]['NTE.3']['NTE.3.1'] != "") {
		channelMap.put('chargeItemId', msg['NTE'][i]['NTE.3']['NTE.3.1']);
	}
	if (msg['NTE'][i]['NTE.4']['NTE.4.2'].toLowerCase() == "casoiniciado" && msg['NTE'][i]['NTE.3']['NTE.3.1'] != "") {
		isInicioEpisodio = true;

	}
}
channelMap.put('isExameEspecial',isExameEspecial);
channelMap.put('isInicioEpisodio', isInicioEpisodio);


// numSequencial
for (var i =0; i < getArrayOrXmlLength(msg['PID']['PID.3']); i++) {
	if ( msg['PID']['PID.3'][i]['PID.3.5'] == "NS") {
		channelMap.put('numSequencial', msg['PID']['PID.3'][i]['PID.3.1']);
	}
}

// nomeDoente
nomeDoente = hexMappingPreProcessor(msg['PID']['PID.5']['PID.5.2'].toString());
if (msg['PID']['PID.5']['PID.5.2'].toString() != "") {
	nomeDoente += " " + hexMappingPreProcessor(msg['PID']['PID.5']['PID.5.2'].toString());
}
nomeDoente += " " + hexMappingPreProcessor(msg['PID']['PID.5']['PID.5.1'].toString());
channelMap.put('nomeDoente', nomeDoente);

