/*
Disntinção entre Exame Especial e Cirurgia: 
	Exame especial = NTE.4.2 = TIPO_MENSAGEM + NTE.3.1 = MCDT
	Cirurgia: outros casos

(Opção 2 de distinção: utilizar checkIfExameEspecial() )

Fluxo de mensagens:
	- se Exame Especial:
		inicio: envia Encounter (status = in-progress)
		final: envia Encounter (status = finished) + ChargeItems
		alta: filtra
	- se Cirurgia:
		inicio: envia Encounter (status = in-progress)
		final: envia Encounter (status = finished)
		alta: Send AddSurgery

	- envio Vigilant:
		não alta: filtra
		alta: envia

select * from CD_Eventos where INTSincroniza = 1
Id|Descr
1|Entrada na sala
2|Início anestesia
4|Início cirurgia
5|Fim cirurgia
6|Fim anestesia
7|Saída sala
8|Chegada do doente
9|Saida do doente
10|Indução
12|Entrada Sala Ind.
14|Saída Sala Ind.
16|Indução LR
100|Entrada na UCPA
101|Saída da UCPA

Destinations: "Send AddSurgery","Send ORM","Send Encounter","Send ChargeItem (Equipa cirurgica)","Send ChargeItem (Tecnica anestesica)"

*/
if (msg['ZHS'][0]['ZHS.22']['ZHS.22.1'].toString() == "S") { // CANCELAMENTO 
	var activeDestinations = ["Send Encounter"];
	// envia-se cancelamento do ChargeItem se o tivermos recebido aquando do agendamento
	if (channelMap.get('chargeItemId') != null) {
		activeDestinations.push("Send ChargeItem (Equipa cirurgica)");
	}
	// envia-se ChargeItem de anestesia, caso a mesma tenha sido aplicada ao doente
	if (msg['ZHS'][0]['ZHS.8']['ZHS.8.1'].toString() != "") {
		activeDestinations.push("Send ChargeItem (Tecnica anestesica)");
	}
	destinationSet.removeAllExcept(activeDestinations);	

} else if ( msg['ZHS'][0]['ZHS.3']['ZHS.3.1'].toString() != "") { // ALTA (fim da cirurgia só enviado aquando da alta)
	var activeDestinations = ["Send ORM","Send Encounter"];
	if ( $('isExameEspecial') == true) { // se exame especial
		activeDestinations.push("Send ChargeItem (Equipa cirurgica)");
		activeDestinations.push("Send ChargeItem (Tecnica anestesica)");
	} else { // se cirurgia
		activeDestinations.push("Send AddSurgery");
	}
	destinationSet.removeAllExcept(activeDestinations);

} else if ($('isInicioEpisodio')) { // mensagem de inicio sai aquando do inicio do episodio!! (momento do clique em "Iniciar Caso")
	var activeDestinations = ["Send Encounter"];
	destinationSet.removeAllExcept(activeDestinations);

} /*else if (channelMap.get('eventoId') == "5") { // FIM
	var activeDestinations = ["Send Encounter"];
	if ( $('isExameEspecial') == true ) { // se exame especial
		activeDestinations.push("Send ChargeItem (Equipa cirurgica)");
		activeDestinations.push("Send ChargeItem (Tecnica anestesica)");
	}
	destinationSet.removeAllExcept(activeDestinations);

}*/else {
	//throw new Error("Erro na definição do tipo de mensagem");
	// trata-se de um evento que não é para ser enviado
	destinationSet.removeAll();
	channelMap.put('isFiltrada', true);
}

