        }]
    },
    verificationStatus: {
        coding: [{
            system: newStringOrUndefined('http:\/\/terminology.hl7.org\/CodeSystem\/condition-ver-status'),
            code: newStringOrUndefined('confirmed')
        }]
    },
    category: [{
        coding: [{
            system: newStringOrUndefined('http:\/\/terminology.hl7.org\/CodeSystem\/condition-category'),
            code: newStringOrUndefined('encounter-diagnosis')
        }]
    }],
    code: {
        coding: [{
            system: newStringOrUndefined("http://hl7.org/fhir/sid/" + (msg['DG1']['DG1.3']['DG1.3.3'].toString() == 'ICD10' ? 'icd-10' : (msg['DG1']['DG1.3']['DG1.3.3'].toString() == 'ICD9' ? 'icd-9-cm' : msg['DG1']['DG1.3']['DG1.3.3'].toString() ) )),
            code: newStringOrUndefined(msg['DG1']['DG1.3']['DG1.3.1'].toString())
        }],
        text: newStringOrUndefined(hexMappingPreProcessor(msg['DG1']['DG1.3']['DG1.3.2'].toString()))
    },
    subject: {
        reference: newStringOrUndefined("Patient/" + msg['PID']['PID.3']['PID.3.1'].toString()),
        identifier: {
            use: newStringOrUndefined('official'),
            type: {
                coding: [{
                    system: newStringOrUndefined($('FhirServer_CUFBUS_Namespace') + '/fhir/CodeSystem/PatientCode'),
                    code: newStringOrUndefined('JMS')
                }],
                text: newStringOrUndefined('JMS Identifier')
            },
            value: newStringOrUndefined(msg['PID']['PID.3']['PID.3.1'].toString())
        },
        display: newStringOrUndefined(msg['PID']['PID.5']['PID.5.2'].toString() + ' ' + msg['PID']['PID.5']['PID.5.1'].toString())
    },
    encounter: {
        reference: newStringOrUndefined("Encounter/" + msg['PV1']['PV1.19']['PV1.19.1'].toString())
    },
    onsetDateTime: newStringOrUndefined(LocalDateTimeToUtc(msg['DG1'][0]['DG1.5']['DG1.5.1'].toString(), "UTC")),
    recordedDate: newStringOrUndefined(LocalDateTimeToUtc(msg['DG1'][0]['DG1.5']['DG1.5.1'].toString(), "UTC"))
};


healthProblemObj.resource = Condition;
$('parameters').push(healthProblemObj);

var count = 0;
for (var i = 0; i < getArrayOrXmlLength(msg['OBR']); i++) {
	// build the Incision
	if (msg['ORC'][i]['ORC.1']['ORC.1.1'] != 'CA') { // Só gera os não cancelados - decisão CUF. Caso se retire esta condição passamos a enviar todos sendo que os cancelados vão com o estado not-done
		count = count + 1;
		incision = buildIncisionFromObr(i, msg['OBR'][i], msg['ORC'][i], count);
		// build the procedure
		service = buildServiceFromObr(i, msg['OBR'][i], incision.resource.id, msg['ORC'], count)

		$('parameters').push(incision);
		$('parameters').push(service);		
	}
}


function buildIncisionFromObr(incisionNumber, obr, orc, count){
	// Incision Id
	var incisionId = UUIDGenerator.getUUID();
	var incision = {};
	incision.name = 'Incision';

	//logger.info("incisionNumber: " + incisionNumber);
	//logger.info("orc[incisionNumber]: " + orc[incisionNumber]);

	var IncisionPerformers = []

	// Cirurgião principal
	var cirurgiaoPrincipal = createPerformerElement(obr['OBR.32']['OBR.32.1']['OBR.32.1.7'].toString(), obr['OBR.32']['OBR.32.1']['OBR.32.1.1'].toString(), (obr['OBR.32']['OBR.32.1']['OBR.32.1.3'].toString() + ' ' + obr['OBR.32']['OBR.32.1']['OBR.32.1.2'].toString()) );
	IncisionPerformers.push(cirurgiaoPrincipal);

	// Outros elementos da equipa
	if (obr['OBR.33'] != null && obr['OBR.33'] != ""){
		for (var i = 0; i < getArrayOrXmlLength(obr['OBR.33']); i++) {
			performerTeamMember = createPerformerElement(obr['OBR.33'][i]['OBR.33.1']['OBR.33.1.7'].toString(), obr['OBR.33'][i]['OBR.33.1']['OBR.33.1.1'].toString(), (obr['OBR.33'][i]['OBR.33.1']['OBR.33.1.3'].toString() + ' ' + obr['OBR.33'][i]['OBR.33.1']['OBR.33.1.2'].toString()))
			IncisionPerformers.push(performerTeamMember);
		}	
	}


	var IncisionProcedure = {
	    resourceType: newStringOrUndefined('Procedure'),
	    id: newStringOrUndefined(incisionId),
	    extension: [{
	            url: newStringOrUndefined($('FhirServer_IMTHOM_Host') + "/fhir/StructureDefinition/Procedure.IncisionLaterality"),
	            valueCoding: {
	                system: newStringOrUndefined($('FhirServer_IMTHOM_Host') + "/fhir/CodeSystem/IncisionLaterality"),
	                code: newStringOrUndefined(orc['ORC.13']['ORC.13.1'] != null ? mapSurgicalIncisionLaterality(orc['ORC.13']['ORC.13.1'].toString()) : mapSurgicalIncisionLaterality(msg['ZHS'][0]['ZHS.15']['ZHS.15.1'].toString()) )
	            }
	        },
	        {
	            url: newStringOrUndefined($('FhirServer_IMTHOM_Host') + "/fhir/StructureDefinition/Procedure.IncisionVia"),
	            valueCoding: {
	                system: newStringOrUndefined($('FhirServer_IMTHOM_Host') + "/fhir/CodeSystem/IncisionVia"),
	                code: newStringOrUndefined(msg['ZHS'][0]['ZHS.19']['ZHS.19.1'].toString())
	            }
	        },
	        {
	            url: newStringOrUndefined($('FhirServer_IMTHOM_Host') + '/fhir/StructureDefinition/Procedure.IncisionNumber'),
	            valueInteger: newNumberOrUndefined(count)
	        }
	    ],
	    status: orc['ORC.1']['ORC.1.1'] == "CA" ? newStringOrUndefined('not-done') : newStringOrUndefined('completed'),
	    subject: {
	        reference: newStringOrUndefined("Patient/" + msg['PID']['PID.3']['PID.3.1'].toString()),
	        identifier: {
	            use: newStringOrUndefined('official'),
	            type: {
	                coding: [{
	                    system: newStringOrUndefined($('FhirServer_CUFBUS_Namespace') + '/fhir/CodeSystem/PatientCode'),
	                    code: newStringOrUndefined('JMS')
	                }],
	                text: newStringOrUndefined('JMS Identifier')
	            },
	            value: newStringOrUndefined(msg['PID']['PID.3']['PID.3.1'].toString())
	        },
	        display: newStringOrUndefined(msg['PID']['PID.5']['PID.5.2'].toString() + ' ' + msg['PID']['PID.5']['PID.5.1'].toString())
	    },
	    encounter: {
	        reference: newStringOrUndefined("Encounter/" + msg['PV1']['PV1.19']['PV1.19.1'].toString())
	    },
	    performer: IncisionPerformers
	};


	incision.resource = IncisionProcedure;

	return incision;
};


function buildServiceFromObr(serviceNumber, obr, relatedIncisionId, orc, count){
	// Service Id (procedure)
	var serviceId = UUIDGenerator.getUUID();
	var service = {};
	service.name = 'Service';

	var ServiceProcedure = {
	    resourceType: newStringOrUndefined('Procedure'),
	    id: newStringOrUndefined(serviceId),
	    extension: [
	        {
	            url: newStringOrUndefined($('FhirServer_IMTHOM_Host') + '/fhir/StructureDefinition/Procedure.ServiceOrder'),
	            valueInteger: newNumberOrUndefined(count)
	        }/*, // Não temos o conceito de produtos consumidos ao nível da alta de cirurgia
	        {
	        	  extension: [
	        	  	{
	                  url: newNumberOrUndefined("Quantity"),
	                  valueQuantity: {
	                  	value: newStringOrUndefined(1)
	                  }
	              },
	              {
	                url: newNumberOrUndefined("Code"),
	                valueCoding: {
	                  system: newNumberOrUndefined($('FhirServer_IMTHOM_Host') + '/fhir/CodeSystem/Product'),
	                  code: newNumberOrUndefined("17ac8749-b21d-ef11-aaf3-40ec99cc75a3"),
	                  display: ("Produto de Teste")
	                }
	              }
	        	  ],
	        	  url: newNumberOrUndefined($('FhirServer_IMTHOM_Host') + '/fhir/StructureDefinition/Procedure.Product')
	        }*/
	    ],
	    status: orc['ORC.5']['ORC.5.1'] == "CA" ? newStringOrUndefined('not-done') : newStringOrUndefined('completed'),
	    partOf: [
          {
            reference: newStringOrUndefined("Procedure/" + relatedIncisionId)
          }
         ],
         code: {
          coding: [
            {
              system: newStringOrUndefined($('FhirServer_IMTHOM_Host') + "/fhir/CodeSystem/Service"),
              code: newStringOrUndefined(getServiceIdFromExtSystem(obr['OBR.4']['OBR.4.1'].toString()))
            }
          ]
         },
	    subject: {
	        reference: newStringOrUndefined("Patient/" + msg['PID']['PID.3']['PID.3.1'].toString()),
	        identifier: {
	            use: newStringOrUndefined('official'),
	            type: {
	                coding: [{
	                    system: newStringOrUndefined($('FhirServer_CUFBUS_Namespace') + '/fhir/CodeSystem/PatientCode'),
	                    code: newStringOrUndefined('JMS')
	                }],
	                text: newStringOrUndefined('JMS Identifier')
	            },
	            value: newStringOrUndefined(msg['PID']['PID.3']['PID.3.1'].toString())
	        },
	        display: newStringOrUndefined(msg['PID']['PID.5']['PID.5.2'].toString() + ' ' + msg['PID']['PID.5']['PID.5.1'].toString())
	    },
	    encounter: {
	        reference: newStringOrUndefined("Encounter/" + msg['PV1']['PV1.19']['PV1.19.1'].toString())
	    }
	};

	service.resource = ServiceProcedure;

	return service;
}


function getPersonFunctionExtSytem(cdFunction){
	var thomPersonFunctionId = null;
	if ($('personFunctionCatalog') != null){
		var catalog = JSON.parse($('personFunctionCatalog'));
		var thomPersonFunctionId = catalog.concept.find(function(concept){
			if (typeof(concept.property) != 'undefined'){
				concept.property.find(function(properties){
						if (properties.valueString == 'CIR'){
							return concept.code;
						}
					}
				)
			}
		});

		return thomPersonFunctionId;
	}
}

function createPerformerElement(personFunction, personNumMecanografico, personName){
	var performerElement = 
	{
		function:{
			coding: [
				{
	                  system: newStringOrUndefined($('FhirServer_CUFBUS_Namespace') + "/fhir/CodeSystem/PersonFunction"),
	                  code: newStringOrUndefined(personFunction) // getPersonFunctionExtSytem
	               }
			]				
		},
		actor: {
			reference: newStringOrUndefined(personNumMecanografico != null ? "Practitioner/" + personNumMecanografico : null),
			identifier: {
	            use: newStringOrUndefined('official'),
	            type: {
	                coding: [{
	                    system: newStringOrUndefined($('FhirServer_CUFBUS_Namespace') + '/fhir/CodeSystem/PersonIdentifierType'),
	                    code: newStringOrUndefined('4')
	                }],
	                text: newStringOrUndefined('N.\u00BA mecanogr\u00E1fico')
	            },
	            value: newStringOrUndefined(personNumMecanografico != null ? personNumMecanografico : null)
	        },
	        display: newStringOrUndefined(personName != null ? personName : null)
		}
	};

	return performerElement;
}

function getServiceIdFromExtSystem(codAto){

	var codExternalSystem = codAto;

	var parameters = {
		resourceType: newStringOrUndefined("Parameters"),
		parameter: [
		   {
		   	name: newStringOrUndefined("system"),
		   	valueUri: newStringOrUndefined($('FhirServer_IMTHOM_Host') + "/fhir/CodeSystem/Service")
		   },
		   {
			   name: newStringOrUndefined("property"),
			   part: [
			       {
				       name: newStringOrUndefined("code"),
				       valueCode: newStringOrUndefined("code")
			       },
			       {
				       name: newStringOrUndefined("value"),
				       valueString: newStringOrUndefined(codAto)
			       }
			   ]
		   }
		]
	}

	var authHeader = null
	if ($('FhirServer_IMTHOM_User') != null && $('FhirServer_IMTHOM_User') != ''){
		authHeader = generateBasicAuthHeader($('FhirServer_IMTHOM_User'), $('FhirServer_IMTHOM_Pass'));	
	}

	var httpResp = httpPost($('FhirServer_IMTHOM_Host') + '/fhir/CodeSystem/$find-matches', JSON.stringify(parameters), authHeader);
	if(httpResp.statusCode === "200"){
		if(isJSON(httpResp.responseMessage)){
			resposta = JSON.parse(httpResp.responseMessage);
			resposta.parameter.find(function(parameter){
				 parameter.part.find(function(parts){
				 	codExternalSystem = parts.valueCoding.code;
				 });
			});

		}
	}

	return codExternalSystem;
}

// Build Resource Parameters
var ParametersResource = {};
ParametersResource.resourceType = 'Parameters';
ParametersResource.parameter = $('parameters');

channelMap.put('ParametersResource', JSON.stringify(consolidate(ParametersResource)));

var statusCode = parseInt( connectorMap.get('responseStatusLine').split(" ")[1] );

if (statusCode == 200 || (statusCode >= 400 && statusCode <= 500) ) { // inclui-se "500", pois podem ser bugs por parte do sistema externo, que não valem a pena fazer retry
	var errDiagnosis = null;
	if (statusCode >= 400 && statusCode <= 500) {
		try {
			errDiagnosis = msg['issue'][0]['diagnostics'];
		} catch (e) {
			;
		}
	}

	//var mess = SerializerFactory.getHL7Serializer().fromXML($('msgOriginal'));
	var mess = SerializerFactory.getSerializer("HL7v2").fromXML($('msgOriginal'));
	var result = router.routeMessageByChannelId("65926965-2044-4d3d-8801-8446e793f27e", RawMessage( mess, null, {"statusCode": statusCode, "errDiagnosis" : errDiagnosis} ) ); // channel: ORR_GENERATE_IN
	if (result != null) {
		if ( result.toString().includes("RECEIVED:")) {
			responseStatus = SENT;
		} else {
			responseStatus = QUEUED;
		}
	} else { // erro no canal seguinte
		channelMap.put('erroRouting', true);
		responseStatus = ERROR;
	}

} else if (statusCode > 500) {
	responseStatus = QUEUED;
} else { // erro
	responseStatus = ERROR;
}

function createPerformerEntry(numMec, role, nome) {
	var performerObj = {};

	var functionObj = {};
	var functionCodingArray = [];
	var functionCodingObj = {};
	functionCodingObj.system = "https://cuf.pt/fhir/CodeSystem/PersonFunction";
	functionCodingObj.code = role;
	functionCodingArray.push(functionCodingObj);
	functionObj.coding = functionCodingArray;
	performerObj.function = functionObj;

	var actorObj= {};
	actorObj.reference = "Practitioner/" + numMec;

	var identifierObj = new Object();
	var codingArray = [];
		var codingObj = new Object();
		codingObj.system = newStringOrUndefined("https://cuf.pt" + '/fhir/CodeSystem/PersonIdentifierType');
		codingObj.code = newStringOrUndefined("4");
		codingArray.push(codingObj);
	var typeObj = new Object();
		typeObj.coding = codingArray;
		typeObj.text = newStringOrUndefined("N.º mecanográfico");
	identifierObj.type = typeObj;
	identifierObj.value = numMec;
	actorObj.identifier = identifierObj;

	actorObj.display = nome;
	performerObj.actor = actorObj;
	return performerObj;
}

