com.mirth.connect.plugins.javascriptstep.script:
var activeDestinations = []

var event = null;
if (msg['entry'].length > 0) {
	for (var i = 0; i < msg['entry'].length; i++){
		if (typeof msg['entry'][i]['resource']['resourceType'] !== 'undefined'){
			if (msg['entry'][i]['resource']['resourceType'] == 'MessageHeader'){
				event = msg['entry'][i]['resource']['eventCoding']['code'];
			}
		}
	}
}

switch (String(event)){

	case "AppointmentCreated" :
		if(!activeDestinations.includes("Urgency Appointment - Send to broker")){
			activeDestinations.push("Urgency Appointment - Send to broker");
		}			
		break;
	case "AppointmentCancelled" :
		if(!activeDestinations.includes("Urgency Appointment - Send to broker")){
			activeDestinations.push("Urgency Appointment - Send to broker");
		}			
		break;
	case "AppointmentEdited" :
		if(!activeDestinations.includes("Urgency Appointment - Send to broker")){
			activeDestinations.push("Urgency Appointment - Send to broker");
		}			
		break;
	case "AppointmentMoved" :
		if(!activeDestinations.includes("Urgency Appointment - Send to broker")){
			activeDestinations.push("Urgency Appointment - Send to broker");
		}			
		break;
	case "VisitCheckInEnded" :
		if(!activeDestinations.includes("Urgency Checkin - Send to broker")){
			activeDestinations.push("Urgency Checkin - Send to broker");
		}			
		break;
	case "VisitCheckInCancelled" :
		if(!activeDestinations.includes("Urgency Checkin - Send to broker")){
			activeDestinations.push("Urgency Checkin - Send to broker");
		}			
		break;
	case "VisitChangeCurrentFacility" :
		if(!activeDestinations.includes("Urgency Change Facility after checkin - VisitChangeCurrentFacility")){
			activeDestinations.push("Urgency Change Facility after checkin - VisitChangeCurrentFacility");
		}			
		break;
	default:
    		break;		
}



destinationSet.removeAllExcept(activeDestinations);

com.mirth.connect.plugins.javascriptrule.script:
//cria default response para ser overridden posteriormente
var response = createOperationOutcome('error', 'processing', 'Internal Server Error', $('serverFhirVersion'), 401, null, false);	

//responseMap.put('response', response);

return true;

// Apenas tratamos Check-ins (Admissões), as mensagens de agendamento apenas tratamos as de Edição ou cancelamento
var isValid = false;

if (msg['entry'].length > 0) {
	for (var i = 0; i < msg['entry'].length; i++){
		if (typeof msg['entry'][i]['resource']['resourceType'] !== 'undefined'){
			if (msg['entry'][i]['resource']['resourceType'] == 'MessageHeader'){

				if ( msg['entry'][i]['resource']['eventCoding']['code'] != 'AppointmentCreated'){
					channelMap.put('evento', msg['entry'][i]['resource']['eventCoding']['code']);
					isValid = true; // porque vamos filtrar na admissão da urgência as mensagens de agendamento, apenas vamos considerar as mensagens de check-in a menos que sejam appointmentCancelled
				}

			}
		}
	}
}

if (isValid == true) {
	return true;	
}
return false;

properties.script:
var inExchangeName = $('RabbitmqExchange_AdmissaoUrgencia');
var inMessageType = $('MessageType_AdmissaoUrgencia');
var inMessagePayload = connectorMessage.getEncodedData();
result = writeQueueRMQ(inExchangeName, inMessageType, inMessagePayload);

if (result.statusCode == "200") {
	response = Response(Status.SENT,  result.statusText);	
} else if (result.statusCode == "400") {
	response = Response(Status.QUEUED,  result.statusText);
} else {
	response = Response(Status.ERROR,  result.statusText);
}

return response;

com.mirth.connect.plugins.javascriptstep.script:
////////////////////////////////	FHIR message: AppointmentCreated	////////////////////////////////
var subsistemaNome = null;			
var listEpisodiosIdentificadores = [];
var agendamentoRefExt = null;

for (var i = 0; i < msg['entry'].length; i++){	//	-----> percorre os vários 'entry'

   if (msg['entry'][i]['resource']['resourceType'] == 'Appointment'){	///	-----> Só 'Appointment'

	// Plano de Saude
	if (typeof msg['entry'][i]['resource']['extension'] != 'undefined'){
		msg['entry'][i]['resource']['extension'].find(function(extension){
			if (extension['url'].includes('Appointment.Encounter')){
				var episodioIdentificador = {};
				episodioIdentificador.sistemaCodificador = 'Encounter.id';
				episodioIdentificador.codigo = extension['valueReference']['reference'].split("/")[1];
				listEpisodiosIdentificadores.push(episodioIdentificador);

				var episodioIdentificador = {};
				episodioIdentificador.sistemaCodificador = 'Encounter.identifier';
				episodioIdentificador.codigo = extension['valueReference']['display'];
				listEpisodiosIdentificadores.push(episodioIdentificador);
			}

			if (extension['url'].includes('HealthSystem')){				
				if (extension['valueReference']['identifier']['system'].includes('cuf') && !extension['valueReference']['identifier']['system'].includes('thom') ){
					tmp['subsistemaRefExt'] = extension['valueReference']['identifier']['value'];
					subsistemaNome = extension['valueReference']['display'];
				}
				tmp['susbsistemaDescr'] = extension['valueReference']['display'];				
			}
			if (typeof (extension['valueReference']) != 'undefined'){
				if (extension['valueReference']['reference'].toLowerCase().includes('coverage')){				
					msg['entry'].find(function(resources){				
						if (resources['resource']['resourceType'] == 'Coverage'){
							tmp['numBeneficiarioSubsistema'] = resources['resource']['subscriberId'];															      
							tmp['dataInicioSubsistema'] =	OffsetTimeToUtc(resources['resource']['period']['start']);							
						}
					})
				}	
			}
		})
	}

	tmp['episodioIdentificadores'] = listEpisodiosIdentificadores;

      ////	Dados do Doente
      for (var j = 0; j < msg['entry'][i]['resource']['participant'].length; j++){  
         if(msg['entry'][i]['resource']['participant'][j]['actor']['reference'].includes("Patient")){
         	   tmp['doente']['nome'] = msg['entry'][i]['resource']['participant'][j]['actor']['display'];
             tmp['doente']['numSequencial'] = msg['entry'][i]['resource']['participant'][j]['actor']['identifier']['value'];             
         }

         if (msg['entry'][i]['resource']['participant'][j]['actor']['reference'].includes('Location')){

		  if (typeof msg['entry'][i]['resource']['participant'][j]['type'] != 'undefined'){

		  	msg['entry'][i]['resource']['participant'][j]['type'].find(function(obj) {
		  		obj['coding'].find(function (coding) {
		  			if (typeof(msg['entry'][i]['resource']['participant'][j]['actor']['identifier']) != 'undefined'  ){
		  				if (coding['code'] === 'HC' ){		  
				  			tmp['localizacao']['hospitalIntRefExt'] = msg['entry'][i]['resource']['participant'][j]['actor']['identifier']['value'];
				  		} else if (coding['code'] === '8'){
				  			tmp['localizacao']['salaGabineteIntRefExt'] = msg['entry'][i]['resource']['participant'][j]['actor']['identifier']['value'];
				  		} else if (coding['code'] === '1') {
				  			tmp['localizacao']['edificioIntRefExt'] = msg['entry'][i]['resource']['participant'][j]['actor']['identifier']['value'];
				  		} else if (coding['code'] === '1'){
				  			tmp['localizacao']['pisoIntRefExt'] = msg['entry'][i]['resource']['participant'][j]['actor']['identifier']['value'];
				  		}	
		  			}

		  		});			  	

			});
		  }

         }
      }

      ////	Agendamento      
      appointmentStatus = mapingAppointmentStatus(msg['entry'][i]['resource']['status']);	
      tmp['estadoIntRefExt'] = appointmentStatus;		// Estado do agendamento
      channelMap.put('appointmentStatus', appointmentStatus);

        ////	Agendamento
	 // agendamentoIdentificador
	 if (typeof msg['entry'][i]['resource']['id'] != "undefined" && msg['entry'][i]['resource']['id'] != "") {
		 agendamentoRefExt = msg['entry'][i]['resource']['id'];	// nº do agendamento
	 }



      if (msg['entry'][i]['resource']['status'].toLowerCase() == 'cancelled'){			// Se estado for cancelado então temos de receber o motivo
         tmp['motivoCancelamentoIntRefExt'] = msg['entry'][i]['resource']['cancelationReason']['coding'][0]['code'];
         tmp['motivoCancelamentoDescr'] = msg['entry'][i]['resource']['cancelationReason']['text'];
      }


	////		Especialidade e subespecialidade
	var areaIdentificadoresList = [];
	msg['entry'][i]['resource']['specialty'].forEach(function(specialty) {
	  specialty['coding'].forEach(function(coding) {
    		var areaIdentificadores = {};
    		if (!coding['system'].trim().toLowerCase().includes('rootfunctionalhealthunit')){
    			areaIdentificadores.sistemaCodificador = coding['system'];
			areaIdentificadores.codigo =  coding['code'];
			areaIdentificadoresList.push(areaIdentificadores);   	
    		}
	  });
	 });  
      tmp['localizacao']['areaIdentificadores'] = areaIdentificadoresList;

      //// Datas (Convert Dates to UTC)
      tmp['dataHoraChegada'] = OffsetTimeToUtc(msg['entry'][i]['resource']['start']);


	////		Serviço
	 msg['entry'][i]['resource']['serviceCategory'].find(function(serviceCategory){
      	serviceCategory['coding'].find(function(coding){ 
      		if (coding['system'].toLowerCase().includes('cuf') && !coding['system'].toLowerCase().includes('thom') ){
      			tmp['servicoIntRefExt'] = coding['code'];
      		}
      	});
      	tmp['servicoDescr'] = serviceCategory['text'];
      });

     if (typeof msg['entry'][i]['resource']['supportingInformation'] != 'undefined'){

     	 // entra no processo de sinistro
		 var episodeOfCare = null;

		 msg['entry'][i]['resource']['supportingInformation'].find( function( supInfo ) {
		 	if(supInfo && supInfo.reference){
				var episodeOfCareStr = supInfo.reference;
				var sinistro = getAccidentInfo(episodeOfCareStr.split("/")[1]);

				if(sinistro.statusCode === "200" && sinistro.tipoSinistro){
					tmp['processoSinistro'] = sinistro.processoSinistro;
				}else {
					tmp['processoSinistro'] = null;
				}

			}
		 });


     }else{
     	//appointment não tem supporting Information
     	tmp['processoSinistro'] = null;
     }

   }///	<----- Só 'Appointment'


	// Subsistema

	if (msg['entry'][i]['resource']['resourceType'] == 'Coverage'){
		if (typeof msg['entry'][i]['resource']['payor'] != 'undefined'){
			msg['entry'][i]['resource']['payor'].find(function(payors){
				tmp['subsistemaDescr'] = payors['display'];
			});
		}
	}

}//	<----- percorre os vários 'entry'


channelMap.put('agendamentoRefExt', validate(agendamentoRefExt, '', new Array()) );
channelMap.put('numSequencial', tmp['doente']['numSequencial']);
channelMap.put('nomeDoente', tmp['doente']['nome']);
channelMap.put('servico', tmp['localizacao']['hospitalIntRefExt']);
channelMap.put('sala', tmp['localizacao']['salaGabineteIntRefExt']);
channelMap.put('temp', tmp);

