var isValid = false;

if (msg['entry'].length > 0) {
	for (var i = 0; i < msg['entry'].length; i++){
		if (typeof msg['entry'][i]['resource']['resourceType'] !== 'undefined'){
			if (msg['entry'][i]['resource']['resourceType'] == 'MessageHeader'){

				if ( msg['entry'][i]['resource']['eventCoding']['code'] === 'AppointmentCreated' || msg['entry'][i]['resource']['eventCoding']['code'] === 'AppointmentCancelled' || msg['entry'][i]['resource']['eventCoding']['code'] === 'AppointmentEdited' || msg['entry'][i]['resource']['eventCoding']['code'] === 'AppointmentMoved' ){
					channelMap.put('evento', msg['entry'][i]['resource']['eventCoding']['code']);
					isValid = true; // false - porque vamos filtrar na admissão da urgência as mensagens de agendamento, apenas vamos considerar as mensagens de check-in a menos que sejam appointmentCancelled
				}

			}
		}
	}
}

if (isValid == true) {
	return true;	
}
return false;

// Filter mandatory fields
var error = false;

for (var i = 0; i < msg['entry'].length; i++){

	if (msg['entry'][i]['resource']['resourceType'] == 'Appointment'){
		var type = "";
		for (var j = 0; j < msg['entry'][i]['resource']['participant'].length; j++){
			 type = type + msg['entry'][i]['resource']['participant'][j]['actor']['reference'];

			if(msg['entry'][i]['resource']['participant'][j]['actor']['reference'].includes("Patient")){
				if (!msg['entry'][i]['resource']['participant'][j]['actor']['identifier']['value'] && msg['entry'][i]['resource']['participant'][j]['actor']['identifier']['value'] == ""){
					var response = createOperationOutcome('error', 'processing', 'Resource.Appointment.Participant[' + j + '].value not defined', $('serverFhirVersion'), 401, null, false);
					error = true;
				}
			}						
		}

		if (!type.includes("Patient")){
			var response = createOperationOutcome('error', 'processing', 'Resource.Appointment.Participant.actor of type Patient not defined', $('serverFhirVersion'), 401, null, false);
			error = true;
		}

		if (!msg['entry'][i]['resource']['id']){
			var response = createOperationOutcome('error', 'processing', 'Resource.Appointment.id not defined', $('serverFhirVersion'), 401, null, false);
			error = true;
		}

		if (!msg['entry'][i]['resource']['status']){
			var response = createOperationOutcome('error', 'processing', 'Resource.Appointment.status not defined', $('serverFhirVersion'), 401, null, false);
			error = true;
		}

		if (msg['entry'][i]['resource']['status'].toLowerCase() == "cancelled"){
			if (typeof(msg['entry'][i]['resource']['cancelationReason']) != 'undefined'){
				if (!msg['entry'][i]['resource']['cancelationReason']['coding'][0]['code']){
					var response = createOperationOutcome('error', 'processing', 'Resource.Appointment.status cancelled must specify a reason', $('serverFhirVersion'), 401, null, false);
					error = true;
				}
			}else {
				var response = createOperationOutcome('error', 'processing', 'Resource.Appointment.status cancelled must specify a reason', $('serverFhirVersion'), 401, null, false);
				error = true;
			}
		}

		if (!msg['entry'][i]['resource']['start']){
			var response = createOperationOutcome('error', 'processing', 'Resource.Appointment.start not defined', $('serverFhirVersion'), 401, null, false);
			error = true;
		}

	}
}

if (error == true){
	return false;
} else {
	var response = createOperationOutcome('information', 'informational', 'Received with success', $('serverFhirVersion'), 201, null, false);
	return true;
}

