com.mirth.connect.plugins.javascriptstep.script:
////////////////////////////////	FHIR message: AppointmentCreated	////////////////////////////////					
var theEncounterId = null;
var theEncounterHumanRedableId = null;
var theAgendamentoRefExt = null;

for (var i = 0; i < msg['entry'].length; i++){	//	-----> percorre os vários 'entry'
	if (msg['entry'][i]['resource']['resourceType'] == 'Encounter'){

		// Doente Info

		tmp['doente']['numSequencial'] = msg['entry'][i]['resource']['subject']['identifier']['value'];
		tmp['doente']['nome'] = msg['entry'][i]['resource']['subject']['display'];

		// episodioIdentificadores
		episodioIdentificadorList = [];
		episodioIdentificadorObj = new Object();
		episodioIdentificadorObj.sistemaCodificador = "Encounter.id";
		episodioIdentificadorObj.codigo = msg['entry'][i]['resource']['id'];
		theEncounterId = msg['entry'][i]['resource']['id'];
		episodioIdentificadorList.push(episodioIdentificadorObj);

		// Human-redable ID
	    	 if (typeof msg['entry'][i]['resource']['identifier'] != 'undefined'){
	    	 	theEncounterHumanRedableId = msg['entry'][i]['resource']['identifier'][0]['value'];
			episodioIdentificadorHumanRedableObj = new Object();
			episodioIdentificadorHumanRedableObj.sistemaCodificador = "Encounter.identifier";
			episodioIdentificadorHumanRedableObj.codigo = theEncounterHumanRedableId;

			episodioIdentificadorList.push(episodioIdentificadorHumanRedableObj);
	    	 }


		tmp['episodioIdentificadores'].push(episodioIdentificadorList);


		////		Datas (Convert Dates to UTC) msg['timestamp']
		tmp['dataHoraChegada'] = OffsetTimeToUtc(msg['timestamp']);

		// Estado
		if ($('evento').trim().toLowerCase().equals('visitcheckincancelled') ){
			tmp['estadoIntRefExt'] = "cancelled";
		} else {
			tmp['estadoIntRefExt'] = msg['entry'][i]['resource']['status'];	
		}

		// Será sempre do tipo URG (urgência) -> embora possamos ir buscar esta informação via API, mas se o fizermos ficamos com uma dependência do THOM num canal de produto
		tmp['servicoIntRefExt'] = "URG";
		tmp['servicoDescr'] = "Urgência";

		/*
		// Motivo de cancelamento -> Não recebemos ao nível do encounter nem é um campo obrigatório para a aplicação
		if (msg['entry'][i]['resource']['status'].toLowerCase() == 'cancelled'){
	         tmp['motivoCancelamentoIntRefExt'] = null;
	         tmp['motivoCancelamentoDescr'] = null;
	     }
		*/

		for (var j = 0; j < msg['entry'][i]['resource']['location'].length; j ++ ){

			msg['entry'][i]['resource']['location'][j]['physicalType']['coding'].find(function(coding) {
					if (coding['code'] == '8'){ // gabinete médico
						if (msg['entry'][i]['resource']['location'][j]['location']['identifier']['system'].includes('cuf')){
							tmp['localizacao']['salaGabineteIntRefExt'] = msg['entry'][i]['resource']['location'][j]['location']['identifier']['value'];	
						}
					}else if (coding['code'] == 'HC'){
						if (msg['entry'][i]['resource']['location'][j]['location']['identifier']['system'].includes('cuf')){
							tmp['localizacao']['hospitalIntRefExt'] = msg['entry'][i]['resource']['location'][j]['location']['identifier']['value'];									
						}
					}
			});	
		}

		// Especialidade de agendamento
		var areaIdentificadoresList = [];
		var areaIdentificadores = {};
		msg['entry'][i]['resource']['extension'].find(function(extensions){
			if (typeof(extensions['valueCoding']) != 'undefined' && typeof(extensions['valueCoding']['system']) != 'undefined' ){
				if (extensions['valueCoding']['system'].toLowerCase().trim().includes('functionalhealthunit') && !extensions['valueCoding']['system'].toLowerCase().trim().includes('rootfunctionalhealthunit')){
					areaIdentificadores.sistemaCodificador = extensions['valueCoding']['system'];
					areaIdentificadores.codigo = extensions['valueCoding']['code'];
					areaIdentificadoresList.push(areaIdentificadores);
				}	
			}
		});

		tmp['localizacao']['areaIdentificadores'] = areaIdentificadoresList;
		tmp['localizacao']['edificioIntRefExt'] = null;
		tmp['localizacao']['pisoIntRefExt'] = null;


		tmp['senha'] = null;
		if (typeof msg['entry'][i]['resource']['extension'] != 'undefined'){
			msg['entry'][i]['resource']['extension'].find(function(extension){
				if (typeof extension['extension'] != 'undefined'){
					extension['extension'].find(function(ext){
						if (ext['url'].toLowerCase().includes('ticket')){
							tmp['senha'] = ext['valueString'];
						}
						if (ext['url'].toLowerCase().includes('categoryid')){
							tmp['senhaCategoriaId'] = ext['valueString'];
						}
					});	
				}	
			});	
		}

		// Professionais
		try{
			if (typeof(msg['entry'][i]['resource']['participant']) != 'undefined'){
				var participants = msg['entry'][i]['resource']['participant'];

				participants.find(function(participant){
					if (participant.individual && participant.individual.reference){
						if (participant.individual.reference.includes('Practitioner')){
							if (participant.individual.identifier && participant.individual.identifier.type && participant.individual.identifier.type.coding ){
								participant.individual.identifier.type.coding.find(function (coding) {
					         	  		if (coding['code'] === '4' ){ // Num Mecanografico do médico (NIF)
					         	  			channelMap.put('numMecanografico', validate(participant.individual.identifier.value));
					         	  		}
					         	  	})	
							}
						}
					}
				})
			}	
		} catch (ex){
			logger.info(ex);
		}


		if (typeof msg['entry'][i]['resource']['episodeOfCare'] != 'undefined'){

			// entra no processo de sinistro
		 	var episodeOfCare = null;

			msg['entry'][i]['resource']['episodeOfCare'].find( function( epCare ) {
				if(epCare && epCare.reference){
					var episodeOfCareStr = epCare.reference;
					var sinistro = getAccidentInfo(episodeOfCareStr.split("/")[1]);

					if(sinistro.statusCode === "200" && sinistro.tipoSinistro){
						tmp['processoSinistro'] = sinistro.processoSinistro;
					}else {
						tmp['processoSinistro'] = null;
					}

				}

			})

		}else{
     		//não tem supporting Information
     		tmp['processoSinistro'] = null;
     	}

		channelMap.put('checkin_status', msg['entry'][i]['resource']['status']);

		channelMap.put('agendamentoRefExt', theAgendamentoRefExt);
		channelMap.put('numSequencial', msg['entry'][i]['resource']['subject']['identifier']['value']);
		channelMap.put('nomeDoente', msg['entry'][i]['resource']['subject']['display']);
		channelMap.put('servico', tmp['localizacao']['hospitalIntRefExt']);
		channelMap.put('sala', tmp['localizacao']['salaGabineteIntRefExt']);
		channelMap.put('senha', tmp['senha']);

	}else if (msg['entry'][i]['resource']['resourceType'] == 'ChargeItem'){

		// save human readable id (Encounter.identifier.value)
		if (typeof msg['entry'][i]['resource']['context'] != 'undefined'){
			if (typeof msg['entry'][i]['resource']['context']['display'] != 'undefined'){

				if (theEncounterHumanRedableId == null){
					episodioIdentificadorHumanRedableObj = new Object();
					episodioIdentificadorHumanRedableObj.sistemaCodificador = "Encounter.identifier";
					episodioIdentificadorHumanRedableObj.codigo = msg['entry'][i]['resource']['context']['display'];

					tmp['episodioIdentificadores'].push(episodioIdentificadorHumanRedableObj);	
				}

			}	
		}

	}

	// Subsistema

	if (msg['entry'][i]['resource']['resourceType'] == 'Coverage'){
		if (typeof msg['entry'][i]['resource']['payor'] != 'undefined'){
			msg['entry'][i]['resource']['payor'].find(function(payors){
				tmp['subsistemaDescr'] = payors['display'];
				tmp['subsistemaRefExt'] = payors['identifier']['value'];
			});
		}

		tmp['dataInicioSubsistema'] = OffsetTimeToUtc(msg['entry'][i]['resource']['period']['start']);
		tmp['numBeneficiarioSubsistema'] = msg['entry'][i]['resource']['subscriberId'];
	}

