/**
	These functions are used with the history interaction.
*/
function getFhirHistoryBaseQuery() {
	var dbType = $cfg('fhirDBDatabaseType');
	if (dbType == 'postgres') {
		return "SELECT name, id, version, data::TEXT, mimetype, deleted, request_method, request_url FROM resource";
	} else if (dbType == 'sqlserver') {
		return "SELECT name, id, version, CAST(data AS NVARCHAR(MAX)) AS data, mimetype, deleted, request_method, request_url FROM resource";
	} else {
		throw 'Unsupported DB type: ' + dbType;
	}
}

function getFhirHistorySinceWherePart() {
	var dbType = $cfg('fhirDBDatabaseType');
	if (dbType == 'postgres') {
		return "last_modified >= ?::TIMESTAMP WITH TIME ZONE";
	} else if (dbType == 'sqlserver') {
		return "last_modified >= CAST(? AS DATETIME)";
	} else {
		throw 'Unsupported DB type: ' + dbType;
	}
}

/**
	Inserts a deleted entry for a resource and returns its DB sequence ID.

	@param {String} type - The FHIR resource type.
	@param {String} id - The logical ID of the resource.
	@param {Number} versionId - The version ID of the resource.
	@param {Date} lastUpdated - The last updated time of the resource.
	@param {String} method - The HTTP method used to create the resource.
	@param {String} url - The HTTP URL used to create the resource.
	@return {Number} The database sequence ID for the inserted resource.
*/
function insertFhirDeletedResource(type, id, versionId, lastUpdated, method, url) {
	var dbType = $cfg('fhirDBDatabaseType');

	if (dbType == 'postgres') {
		var params = [type, id, versionId, lastUpdated, $('method'), $('url')];
		var result = executeUpdateAndGetGeneratedKeys("INSERT INTO resource (name, id, version, last_modified, deleted, request_method, request_url) VALUES (?, ?, ?, ?, TRUE, ?, ?)", params);
		result.next();
		return result.getInt(1);
	} else if (dbType == 'sqlserver') {
		var dbConn = createFHIRDBConnection();

		try {
			dbConn.setAutoCommit(false);

			var idResult = executeCachedQuery('SELECT id FROM resource_sequence WITH (UPDLOCK)', [], dbConn);
			idResult.next();
			var sequenceId = idResult.getInt(1);
			executeUpdate('UPDATE resource_sequence SET ID = ID + 1', [], dbConn);
			dbConn.commit();

			var params = [sequenceId, type, id, versionId, lastUpdated, $('method'), $('url')];
			executeUpdate("INSERT INTO resource (sequence_id, name, id, version, last_modified, deleted, request_method, request_url) VALUES (?, ?, ?, ?, ?, 1, ?, ?)", params, dbConn);
			dbConn.commit();

			return sequenceId;
		} catch (e) {
			try { dbConn.rollback() } catch (e2) {}
			throw e;
		} finally {
			try { dbConn.close() } catch (e) {}
		}
	} else {
		throw 'Unsupported DB type: ' + dbType;
	}
}

/**
	Inserts a new resource and returns its DB sequence ID.

	@param {String} type - The FHIR resource type.
	@param {String} id - The logical ID of the resource.
	@param {Number} versionId - The version ID of the resource.
	@param {Date} lastUpdated - The last updated time of the resource.
	@param {String} data - The resource data.
	@param {String} contentType - The MIME type of the resource data.
	@param {String} method - The HTTP method used to create the resource.
	@param {String} url - The HTTP URL used to create the resource.
	@return {Number} The database sequence ID for the inserted resource.
*/
function insertFhirResource(type, id, versionId, lastUpdated, data, contentType, method, url) {
	var dbType = $cfg('fhirDBDatabaseType');

	if (dbType == 'postgres') {
		var params = [type, id, versionId, lastUpdated, data, contentType, method, url];
		var result = executeUpdateAndGetGeneratedKeys("INSERT INTO resource (name, id, version, last_modified, data, mimetype, request_method, request_url) VALUES (?, ?, ?, ?, ?::XML, ?, ?, ?)", params);
		result.next();
		return result.getInt(1);
	} else if (dbType == 'sqlserver') {
		var dbConn = createFHIRDBConnection();

		try {
			dbConn.setAutoCommit(false);

			var idResult = executeCachedQuery('SELECT id FROM resource_sequence WITH (UPDLOCK)', [], dbConn);
			idResult.next();
			var sequenceId = idResult.getInt(1);
			executeUpdate('UPDATE resource_sequence SET ID = ID + 1', [], dbConn);
			dbConn.commit();

			var params = [sequenceId, type, id, versionId, lastUpdated, data, contentType, method, url];
			executeUpdate("INSERT INTO resource (sequence_id, name, id, version, last_modified, data, mimetype, request_method, request_url) VALUES (?, ?, ?, ?, ?, CAST(? AS XML), ?, ?, ?)", params, dbConn);
			dbConn.commit();

			return sequenceId;
		} catch (e) {
			try { dbConn.rollback() } catch (e2) {}
			throw e;
		} finally {
			try { dbConn.close() } catch (e) {}
		}
	} else {
		throw 'Unsupported DB type: ' + dbType;
	}
}

/**
	These functions are used with the search interaction.
*/
function getFhirSearchBaseQuery(usingLimit) {
	var dbType = $cfg('fhirDBDatabaseType');
	if (dbType == 'postgres') {
		return "SELECT name, id, version, data::TEXT, mimetype, request_method, request_url FROM resource r1 WHERE version = (SELECT MAX(version) FROM resource r2 WHERE r2.name = r1.name AND r2.id = r1.id) AND (deleted IS NULL OR deleted = FALSE)";
	} else if (dbType == 'sqlserver') {
		var query = "SELECT name, id, version, CAST(data AS NVARCHAR(MAX)) AS data, mimetype, request_method, request_url";
		if (usingLimit) {
			query += ", ROW_NUMBER() OVER (" + getOrderClause() + ") AS ROWNUM";
		}
		return query + " FROM resource r1 WHERE version = (SELECT MAX(version) FROM resource r2 WHERE r2.name = r1.name AND r2.id = r1.id) AND (deleted IS NULL OR deleted = 0)";
	} else {
		throw 'Unsupported DB type: ' + dbType;
	}
}

function getFhirSearchContentWherePart() {
	var dbType = $cfg('fhirDBDatabaseType');
	if (dbType == 'postgres') {
		return "data::TEXT ILIKE ?";
	} else if (dbType == 'sqlserver') {
		return "UPPER(CAST(data AS NVARCHAR(MAX))) LIKE UPPER(?)";
	} else {
		throw 'Unsupported DB type: ' + dbType;
	}
}

function getFhirSearchLastUpdatedWherePart(lastUpdatedOperator) {
	var dbType = $cfg('fhirDBDatabaseType');
	if (dbType == 'postgres') {
		return "last_modified " + convertFhirParameterOperator(lastUpdatedOperator) + " ?::TIMESTAMP WITH TIME ZONE";
	} else if (dbType == 'sqlserver') {
		return "last_modified " + convertFhirParameterOperator(lastUpdatedOperator) + " CAST(? AS DATETIMEOFFSET)";
	} else {
		throw 'Unsupported DB type: ' + dbType;
	}
}

function convertFhirParameterOperator(operator) {
	switch ((operator + '').toLowerCase()) {
		case 'eq': return '=';
		case 'ne': return '!=';
		case 'gt': return '>';
		case 'lt': return '<';
		case 'ge': return '>=';
		case 'le': return '<=';
		case 'sa': return '>';
		case 'eb': return '<';
		case 'ap': return '=';
		default: return '=';
	}
}

function convertXPathNodeSingleton(node) {
	var dbType = $cfg('fhirDBDatabaseType');
	if (dbType == 'postgres') {
		return node;
	} else if (dbType == 'sqlserver') {
		return node.split('/').join('[1]/');
	} else {
		throw 'Unsupported DB type: ' + dbType;
	}
}

function xpathLowerCase(value) {
	var dbType = $cfg('fhirDBDatabaseType');
	if (dbType == 'postgres') {
		return "translate(" + value + ", 'ABCDEFGHIJKLMNOPQRSTUVWXYZ', 'abcdefghijklmnopqrstuvwxyz')";
	} else if (dbType == 'sqlserver') {
		return "fn:lower-case(" + value + ")";
	} else {
		throw 'Unsupported DB type: ' + dbType;
	}
}

function getFhirSearchXPathWherePart() {
	var dbType = $cfg('fhirDBDatabaseType');
	if (dbType == 'postgres') {
		return "XPATH_EXISTS(?, data)";
	} else if (dbType == 'sqlserver') {
		return "data.exist(?) = 1";
	} else {
		throw 'Unsupported DB type: ' + dbType;
	}
}

function addXPath(xpath, whereParts, params) {
	var dbType = $cfg('fhirDBDatabaseType');
	if (dbType == 'postgres') {
		whereParts.push(getFhirSearchXPathWherePart());
		params.push(xpath);
	} else if (dbType == 'sqlserver') {
		whereParts.push(getFhirSearchXPathWherePart().replace('?', "'" + xpath.replace(/'/g, "''") + "'"));
	} else {
		throw 'Unsupported DB type: ' + dbType;
	}
}

function addOrder(query, usingLimit) {
	var dbType = $cfg('fhirDBDatabaseType');
	if (dbType == 'postgres') {
		return query + " " + getOrderClause();
	} else if (dbType == 'sqlserver') {
		if (usingLimit) {
			return query;
		} else {
			return query + " " + getOrderClause();
		}
	} else {
		throw 'Unsupported DB type: ' + dbType;
	}
}

function getOrderClause() {
	return "ORDER BY name ASC, id ASC, version DESC";
}

function addLimit(query, limit) {
	var dbType = $cfg('fhirDBDatabaseType');
	if (dbType == 'postgres') {
		return query + " LIMIT " + limit;
	} else if (dbType == 'sqlserver') {
		return "SELECT A.* FROM (" + query + ") AS A WHERE A.ROWNUM <= " + limit;
	} else {
		throw 'Unsupported DB type: ' + dbType;
	}
}

/**
	Returns a ResultSet for a specific resource instance.

	@param {String} type - The FHIR resource type.
	@param {String} id - The logical ID of the resource.
	@return {ResultSet} The ResultSet.
*/
function getResource(type, id) {
	var params = [type, id];
	var dbType = $cfg('fhirDBDatabaseType');

	if (dbType == 'postgres') {
		return executeCachedQuery("SELECT version, data::TEXT, mimetype, last_modified, deleted FROM resource WHERE name = ? AND id = ? ORDER BY version DESC LIMIT 1", params);
	} else if (dbType == 'sqlserver') {
		return executeCachedQuery("SELECT TOP 1 version, CAST(data AS NVARCHAR(MAX)) AS data, mimetype, last_modified, deleted FROM resource WHERE name = ? AND id = ? ORDER BY version DESC", params);
	} else {
		throw 'Unsupported DB type: ' + dbType;
	}
}

/**
	Returns a ResultSet for a specific resource instance at a specific version.

	@param {String} type - The FHIR resource type.
	@param {String} id - The logical ID of the resource.
	@param {Number} versionId - The version ID of the resource.
	@return {ResultSet} The ResultSet.
*/
function getResourceVersion(type, id, versionId) {
	var params = [type, id, parseInt(versionId, 10)];
	var dbType = $cfg('fhirDBDatabaseType');

	if (dbType == 'postgres') {
		return executeCachedQuery("SELECT data::TEXT, mimetype, last_modified, deleted FROM resource WHERE name = ? AND id = ? AND version = ?", params);
	} else if (dbType == 'sqlserver') {
		return executeCachedQuery("SELECT CAST(data AS NVARCHAR(MAX)) AS data, mimetype, last_modified, deleted FROM resource WHERE name = ? AND id = ? AND version = ?", params);
	} else {
		throw 'Unsupported DB type: ' + dbType;
	}
}

/**
	Returns the version ID for a given resource, or 0 if it doesn't exist.

	@param {String} type - The FHIR resource type.
	@param {String} id - The logical ID of the resource.
	@return {Number} The version ID, or 0 if the resource doesn't exist.
*/
function getFhirVersion(type, id) {
	var params = [type, id];
	var result;

	var dbType = $cfg('fhirDBDatabaseType');
	if (dbType == 'postgres') {
		result = executeCachedQuery("SELECT version FROM resource WHERE name = ? AND id = ? ORDER BY version DESC LIMIT 1", params);
	} else if (dbType == 'sqlserver') {
		result = executeCachedQuery("SELECT TOP 1 version FROM resource WHERE name = ? AND id = ? ORDER BY version DESC", params);
	} else {
		throw 'Unsupported DB type: ' + dbType;
	}

	if (result.next()) {
		return result.getInt(1);
	}
	return 0;
}

/**
	Returns a ResultSet with the version and deleted flag for a given resource.

	@param {String} type - The FHIR resource type.
	@param {String} id - The logical ID of the resource.
	@return {ResultSet} The ResultSet.
*/
function getFhirVersionAndDeleted(type, id) {
	var params = [type, id];
	var dbType = $cfg('fhirDBDatabaseType');
	if (dbType == 'postgres') {
		return executeCachedQuery("SELECT version, deleted FROM resource WHERE name = ? AND id = ? ORDER BY version DESC LIMIT 1", params);
	} else if (dbType == 'sqlserver') {
		return executeCachedQuery("SELECT TOP 1 version, deleted FROM resource WHERE name = ? AND id = ? ORDER BY version DESC", params);
	} else {
		throw 'Unsupported DB type: ' + dbType;
	}
}

/**
	Setting the Encounter Identifier Value by Encounter Id (returns true: inserted; false: not inserted)

	@param {String} encounterIdentifierValue - Encounter Identifier Value
	@param {String} encounterId - Encounter Id
	@return {Boolean} return true if inserts or false if not
*/
function setEncounterIdentifierValueByEncounterId(encounterIdentifierValue, encounterId){

	if (getEncounterIdentifierValueByEncounterId(encounterId) != null) {
		updateEncounterIdentifierValueByEncounterId(encounterIdentifierValue, encounterId);
	} else { // set/insert

		var res = null;
		// Define the PostgreSQL JDBC URL, username, and password
		var url = "jdbc:postgresql://" + $('DB_transactions_host') + ":" + $('DB_transactions_port') + "/" + $('DB_transactions_name');
		var username = $('DB_transactions_user');
		var password = $('DB_transactions_pass');

		// Load the PostgreSQL JDBC driver
		java.lang.Class.forName("org.postgresql.Driver");

		// Establish a database connection
		var conn = java.sql.DriverManager.getConnection(url, username, password);

		try {
			// Define the SQL query to retrieve the value by ID
			// var insertQuery = "INSERT INTO episod_ids_mapping (human_episod_id, uuid_episod) VALUES (?,?) ON CONFLICT (uuid_episod) DO NOTHING"; // ?? trocar isto
			var insertQuery = "INSERT INTO encounter_external_ids (encounter_identifier_value, encounter_id) VALUES (?,?)";

			// Create a prepared statement
			var pstmt = conn.prepareStatement(insertQuery);
			pstmt.setString(1, encounterIdentifierValue);
			pstmt.setString(2, encounterId);

			// Execute the query and retrieve the result
			var rowsInserted = pstmt.executeUpdate();

			if (rowsInserted > 0) {
				   res = true;
			        //logger.info("New row inserted with value: " + newValue);
			    } else {
			    	   res = false;
			        //logger.info("No rows inserted.");
			    }
			} catch (e) {
			    logger.error("Error inserting row: " + e);
			    res = false;
			} finally {
			    // Close the resources
			    if (pstmt) pstmt.close();
			    if (conn) conn.close();
			}


		return res;
	}
}

/**
	Setting the HL7 Placer Id by ServiceRequest resource Id (returns true: inserted; false: not inserted)

	@param {String} serviceRequest - serviceRequest resource Id
	@param {String} HL7 Placer Id
	@return {Boolean} return true if inserts or false if not
*/
function setHl7PlacerByServiceRequestId(serviceRequest, hl7Placer){
	var res = null;
	// Define the PostgreSQL JDBC URL, username, and password
	var url = "jdbc:postgresql://" + $('DB_transactions_host') + ":" + $('DB_transactions_port') + "/" + $('DB_transactions_name');
	var username = $('DB_transactions_user');
	var password = $('DB_transactions_pass');

	// Load the PostgreSQL JDBC driver
	java.lang.Class.forName("org.postgresql.Driver");

	// Establish a database connection
	var conn = java.sql.DriverManager.getConnection(url, username, password);

	try {
		// Define the SQL query to retrieve the value by ID
		var insertQuery = "INSERT INTO service_request_external_ids (service_request_id, hl7_placer) VALUES (?,?) ON CONFLICT (service_request_id) DO NOTHING";

		// Create a prepared statement
		var pstmt = conn.prepareStatement(insertQuery);
		pstmt.setString(1, serviceRequest);
		pstmt.setString(2, hl7Placer);

		// Execute the query and retrieve the result
		var rowsInserted = pstmt.executeUpdate();

		if (rowsInserted > 0) {
			   res = true;
		        //logger.info("New row inserted with value: " + newValue);
		    } else {
		    	   res = false;
		        //logger.info("No rows inserted.");
		    }
		} catch (e) {
		    logger.error("Error inserting row: " + e);
		    res = false;
		} finally {
		    // Close the resources
		    if (pstmt) pstmt.close();
		    if (conn) conn.close();
		}


	return res;
}

/**
	Updating the Encounter Identifier Value by Encounter Id (returns true: inserted; false: not updated)

	@param {String} encounterIdentifierValue - Encounter Identifier Value
	@param {String} encounterId - Encounter Id
	@return {Boolean} return true if updates or false if not
*/
function updateEncounterIdentifierValueByEncounterId(encounterIdentifierValue, encounterId){
	var res = null;
	// Define the PostgreSQL JDBC URL, username, and password
	var url = "jdbc:postgresql://" + $('DB_transactions_host') + ":" + $('DB_transactions_port') + "/" + $('DB_transactions_name');
	var username = $('DB_transactions_user');
	var password = $('DB_transactions_pass');

	// Load the PostgreSQL JDBC driver
	java.lang.Class.forName("org.postgresql.Driver");

	// Establish a database connection
	var conn = java.sql.DriverManager.getConnection(url, username, password);

	try {
		// Define the SQL query to retrieve the value by ID
		var insertQuery = "UPDATE encounter_external_ids set encounter_identifier_value = ? where encounter_id = ?;"; // ?? trocar

		// Create a prepared statement
		var pstmt = conn.prepareStatement(insertQuery);
		pstmt.setString(1, encounterIdentifierValue);
		pstmt.setString(2, encounterId);

		// Execute the query and retrieve the result
		var rowsUpdated = pstmt.executeUpdate();

		if (rowsUpdated > 0) {
			   res = true;
		        //logger.info("New row inserted with value: " + newValue);
		    } else {
		    	   res = false;
		        //logger.info("No rows inserted.");
		    }
		} catch (e) {
		    logger.error("Error inserting row: " + e);
		    res = false;
		} finally {
		    // Close the resources
		    if (pstmt) pstmt.close();
		    if (conn) conn.close();
		}


	return res;
}

