	var statusMessage = "\n POST " + url + '\n' + '[BODY]\n' + connectorMessage.getEncodedData();

	if (parseInt(result.statusCode) >= 500) {
		response = Response(Status.ERROR,  result.responseMessage, statusMessage);
		return response;
	} else if (parseInt(result.statusCode) >= 200 && parseInt(result.statusCode) < 400) {
		response = Response(Status.SENT,  result.responseMessage, statusMessage);
		return response;
	} else {
		response = Response(Status.ERROR,  result.responseMessage, statusMessage);
		return response;
	}

}else {
	response = Response(Status.QUEUED, $('errorMsg'));
	return response;
}

