          }

	@param {Object} utilizadores - informação dos utilizadores:
		[{
		      "nome" : null,
		      "numMecanografico" : null,
		      "perfil" : null
		 }]

	@param {Object} infosRequisicao - informações gerais relativas à requisição:
		{
		      "criar": true,
		      "requisicaoId": null,
		      "requisicaoRefExt": null,
